// frontend/src/pages/AboutPage.js
import React, { useState, useEffect } from 'react';
import api from '../services/api'; // Your Axios instance
// import { useAuth } from '../contexts/AuthContext'; // For potential future admin edit links on this page

// --- Placeholder Data for sections NOT YET DYNAMIC ---
const aboutMeData = {
  introduction: [
    "Hello! I'm Harisharan, a passionate Full Stack Developer with a keen interest in building efficient, scalable, and user-friendly web applications. My journey in tech has been driven by a continuous desire to learn and apply new technologies to solve real-world problems.",
    "I specialize in the MERN stack (MongoDB, Express.js, React, Node.js) and have a strong foundation in JavaScript, along with experience in building responsive and dynamic user interfaces with Tailwind CSS.",
    "I thrive on turning complex ideas into tangible digital experiences. This portfolio showcases some of my work and the skills I've developed. I'm always eager to learn, adapt, and collaborate on exciting projects."
  ],
  philosophy: "I believe that the best solutions come from a blend of technical expertise, a deep understanding of user needs, and a commitment to clean, maintainable code."
};

// Skills, Achievements, Certifications will be made dynamic later
const skillsData = {
  "Languages": ["JavaScript (ES6+)", "Python", "Java", "C++", "HTML5", "CSS3", "SQL"],
  "Frameworks & Libraries": ["React", "Node.js", "Express.js", "Tailwind CSS", "Mongoose"],
  "Databases": ["MongoDB"],
  "Tools & Platforms": ["Git & GitHub", "VS Code", "Postman"],
  "Concepts": ["RESTful APIs", "Agile Methodologies", "Problem Solving"]
};
const achievementsData = ["Successfully developed X full-stack applications.", "Led Y project to completion."];
const certificationsData = [{id: "cert1", name: "Placeholder Cert", issuingOrganization: "Org", dateIssued: "Date"}];
// --- End Placeholder Data ---


const AboutPage = () => {
  // const { isAdmin } = useAuth(); // For admin edit links on this page, if needed later

  // State for Education section
  const [educationHistory, setEducationHistory] = useState([]);
  const [isLoadingEducation, setIsLoadingEducation] = useState(true);
  const [errorEducation, setErrorEducation] = useState(null);

  // State for Skills section (if you want to make it dynamic next)
  const [skillCategories, setSkillCategories] = useState([]);
  const [isLoadingSkills, setIsLoadingSkills] = useState(true);
  const [errorSkills, setErrorSkills] = useState(null);


  useEffect(() => {
    const fetchEducation = async () => {
      setIsLoadingEducation(true);
      setErrorEducation(null);
      try {
        const { data } = await api.get('/education'); // Fetch from your backend
        setEducationHistory(data);
      } catch (err) {
        console.error("Error fetching education history for About page:", err);
        setErrorEducation(err.response?.data?.message || "Could not load education history.");
      } finally {
        setIsLoadingEducation(false);
      }
    };

    const fetchSkillCategories = async () => {
      setIsLoadingSkills(true);
      setErrorSkills(null);
      try {
        const { data } = await api.get('/skillcategories'); // Fetch from your backend
        setSkillCategories(data);
      } catch (err) {
        console.error("Error fetching skill categories for About page:", err);
        setErrorSkills(err.response?.data?.message || "Could not load skills.");
      } finally {
        setIsLoadingSkills(false);
      }
    };

    fetchEducation();
    fetchSkillCategories(); // Call to fetch skills
  }, []);

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-16 space-y-16 md:space-y-24">

      <div className="text-center">
        <h1 className="text-4xl sm:text-5xl font-bold text-gray-800 dark:text-white mb-4">About Me</h1>
        <p className="text-lg text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">A glimpse into my journey, skills, and aspirations.</p>
      </div>

      {/* Who I Am Section (still using aboutMeData object for now) */}
      <section id="who-i-am" className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-xl">
        <h2 className="text-3xl font-semibold text-gray-800 dark:text-gray-100 mb-6">Who I Am</h2>
        <div className="space-y-4 text-gray-700 dark:text-gray-300 leading-relaxed text-base sm:text-lg">
          {aboutMeData.introduction.map((paragraph, index) => (<p key={index}>{paragraph}</p>))}
          {aboutMeData.philosophy && <p className="mt-6 italic border-l-4 border-indigo-500 dark:border-indigo-400 pl-4 py-3 bg-indigo-50 dark:bg-slate-700/60 rounded-r-md">"{aboutMeData.philosophy}"</p>}
        </div>
      </section>

      {/* Skills Section - Displaying Dynamic Data */}
      <section id="skills">
        <h2 className="text-3xl font-bold text-gray-800 dark:text-white mb-10 text-center">Technical Skills</h2>
        {isLoadingSkills && <p className="text-center dark:text-gray-400">Loading skills...</p>}
        {errorSkills && <p className="text-center text-red-500 dark:text-red-400">{errorSkills}</p>}
        {!isLoadingSkills && !errorSkills && skillCategories.length === 0 && (
          <p className="text-center text-gray-500 dark:text-gray-400">No skills listed yet.</p>
        )}
        {!isLoadingSkills && !errorSkills && skillCategories.length > 0 && (
          <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-xl">
            {skillCategories.map((category) => (
              <div key={category._id} className="mb-8 last:mb-0">
                <h3 className="text-2xl font-semibold text-indigo-700 dark:text-indigo-400 mb-4 border-b-2 border-indigo-200 dark:border-slate-700 pb-2">
                  {category.name}
                </h3>
                {category.skills && category.skills.length > 0 ? (
                  <div className="flex flex-wrap gap-3">
                    {category.skills.map((skill, index) => (
                      <span 
                        key={`${category._id}-${skill}-${index}`}
                        className="bg-indigo-100 dark:bg-slate-700 text-indigo-800 dark:text-indigo-200 px-4 py-2 rounded-full text-sm font-medium shadow-sm"
                      >
                        {skill}
                      </span>
                    ))}
                  </div>
                ) : (
                  <p className="text-sm text-gray-500 dark:text-gray-400">No skills listed in this category yet.</p>
                )}
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Education Section - Displaying Dynamic Data */}
      <section id="education">
        <h2 className="text-3xl font-bold text-gray-800 dark:text-white mb-10 text-center">Education</h2>
        {isLoadingEducation && <p className="text-center dark:text-gray-400">Loading education...</p>}
        {errorEducation && <p className="text-center text-red-500 dark:text-red-400">{errorEducation}</p>}
        {!isLoadingEducation && !errorEducation && educationHistory.length === 0 && (
          <p className="text-center text-gray-500 dark:text-gray-400">No education history to display. Add entries in the admin panel.</p>
        )}
        {!isLoadingEducation && !errorEducation && educationHistory.length > 0 && (
          <div className="space-y-8">
            {educationHistory.map((edu) => (
              <div key={edu._id} className="bg-white dark:bg-slate-800 p-6 rounded-xl shadow-xl transition-all duration-300 hover:shadow-2xl border border-gray-200 dark:border-slate-700 hover:border-indigo-300 dark:hover:border-indigo-500">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-2">
                  <h3 className="text-xl font-semibold text-gray-800 dark:text-gray-100">{edu.degree}</h3>
                  <p className="text-sm text-indigo-600 dark:text-indigo-400 mt-1 sm:mt-0 whitespace-nowrap">{edu.startDate} - {edu.endDate}</p>
                </div>
                <p className="text-md font-medium text-gray-700 dark:text-gray-300">{edu.institution}</p>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-3">{edu.location}</p>
                {edu.gpa && <span className="inline-block bg-gray-200 dark:bg-slate-700 text-gray-700 dark:text-gray-300 px-3 py-1 rounded-full text-xs font-semibold mb-3">GPA: {edu.gpa}</span>}
                {edu.description && <p className="mt-2 text-sm text-gray-600 dark:text-gray-400 leading-relaxed">{edu.description}</p>}
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Certifications Section (still using placeholder data) */}
      <section id="certifications">
        <h2 className="text-3xl font-bold text-gray-800 dark:text-white mb-10 text-center">Licenses & Certifications</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {certificationsData.length > 0 ? certificationsData.map((cert) => (
            <div key={cert.id} className="bg-white dark:bg-slate-800 p-6 rounded-xl shadow-xl flex flex-col items-start transition-all duration-300 hover:shadow-2xl border border-gray-200 dark:border-slate-700 hover:border-green-300 dark:hover:border-green-500">
              <h3 className="text-lg font-semibold text-gray-800 dark:text-gray-100">{cert.name}</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">{cert.issuingOrganization}</p>
              <p className="text-xs text-gray-500 dark:text-gray-500 mt-1 mb-3">Issued: {cert.dateIssued}</p>
              {cert.credentialUrl && cert.credentialUrl !== "#" ? ( <a href={cert.credentialUrl} target="_blank" rel="noopener noreferrer" className="mt-auto inline-block text-sm text-green-600 dark:text-green-400 hover:text-green-700 dark:hover:text-green-300 font-medium transition-colors">View Credential &rarr;</a>) : (<span className="mt-auto text-sm text-gray-400 dark:text-gray-600">(No online credential)</span>)}
            </div>
          )) : ( <p className="md:col-span-2 text-center text-gray-500 dark:text-gray-400">No certifications listed yet.</p> )}
        </div>
      </section>

      {/* Achievements Section (still using placeholder data) */}
      <section id="achievements">
        <h2 className="text-3xl font-bold text-gray-800 dark:text-white mb-10 text-center">Key Achievements</h2>
        <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-xl">
          <ul className="space-y-4 list-inside">
            {achievementsData.map((achievement, index) => (
              <li key={index} className="flex items-start text-gray-700 dark:text-gray-300 text-base">
                <span className="text-indigo-500 dark:text-indigo-400 mr-3 text-xl leading-none mt-1">&rarr;</span>
                <span>{achievement}</span>
              </li>
            ))}
          </ul>
        </div>
      </section>
    </div>
  );
};

export default AboutPage;
