// frontend/src/pages/ProjectsPage.js
import React, { useState, useEffect } from 'react';
import ProjectList from '../components/projects/ProjectList';
import LoadingSpinner from '../components/common/LoadingSpinner';
import api from '../services/api'; // Assuming you have src/services/api.js

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        setLoading(true);
        const { data } = await api.get('/projects'); // Fetches from your backend
        setProjects(data);
        setError(null);
      } catch (err) {
        console.error("Error fetching projects:", err);
        setError(err.response?.data?.message || err.message || 'Could not fetch projects.');
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-10 text-center">My Projects</h1>
      {loading && <LoadingSpinner />}
      {error && <div className="text-center text-red-500 bg-red-100 p-4 rounded-md shadow">{error}</div>}
      {!loading && !error && (
        projects.length > 0 ? (
          <ProjectList projects={projects} />
        ) : (
          <p className="text-center text-gray-600">No projects to display at the moment. Check back soon!</p>
        )
      )}
    </div>
  );
};

export default ProjectsPage;
