// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';

// --- Edit Modals (defined at the top level of the module) ---
const EditTextModal = ({ show, onClose, currentSettings, onSave, isLoading }) => {
  const [formData, setFormData] = useState({
    ownerName: '', jobTitle: '', specialization: '', homePageIntroParagraph: '',
  });

  useEffect(() => {
    if (show && currentSettings) {
      setFormData({
        ownerName: currentSettings.ownerName || '',
        jobTitle: currentSettings.jobTitle || '',
        specialization: currentSettings.specialization || '',
        homePageIntroParagraph: currentSettings.homePageIntroParagraph || '',
      });
    }
  }, [show, currentSettings]);

  if (!show) return null;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-75 z-50 flex justify-center items-center p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-lg animate-modalFadeIn">
        <style jsx global>{` @keyframes modalFadeIn { to { opacity: 1; transform: scale(1); } } .animate-modalFadeIn { animation: modalFadeIn 0.2s ease-out forwards; opacity:0; transform: scale(0.95); } `}</style>
        <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-semibold text-gray-800">Edit Homepage Content</h2>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600 text-2xl leading-none p-1">&times;</button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="ownerNameModal" className="block text-sm font-medium text-gray-700">Your Name</label>
            <input type="text" name="ownerName" id="ownerNameModal" value={formData.ownerName} onChange={handleChange} className="mt-1 input-style w-full" />
          </div>
          <div>
            <label htmlFor="jobTitleModal" className="block text-sm font-medium text-gray-700">Your Profession/Title</label>
            <input type="text" name="jobTitle" id="jobTitleModal" value={formData.jobTitle} onChange={handleChange} className="mt-1 input-style w-full" />
          </div>
          <div>
            <label htmlFor="specializationModal" className="block text-sm font-medium text-gray-700">Specialization/Tagline</label>
            <input type="text" name="specialization" id="specializationModal" value={formData.specialization} onChange={handleChange} className="mt-1 input-style w-full" />
          </div>
          <div>
            <label htmlFor="homePageIntroParagraphModal" className="block text-sm font-medium text-gray-700">Introductory Paragraph</label>
            <textarea name="homePageIntroParagraph" id="homePageIntroParagraphModal" rows="4" value={formData.homePageIntroParagraph} onChange={handleChange} className="mt-1 input-style w-full"></textarea>
          </div>
          <div className="flex justify-end space-x-3 pt-2">
            <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
            <button type="submit" disabled={isLoading} className="btn-primary disabled:opacity-70">
              {isLoading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const EditPhotoModal = ({ show, onClose, currentPhotoUrl, onSave, isLoading }) => {
  const [selectedPhotoFile, setSelectedPhotoFile] = useState(null);

  useEffect(() => {
    if (!show) { 
      setSelectedPhotoFile(null);
      const fileInput = document.getElementById('profilePhotoFileModalInput'); // Ensure this ID is unique if multiple file inputs
      if (fileInput) fileInput.value = null;
    }
  }, [show]);

  if (!show) return null; 

  const handleSubmit = (e) => {
    e.preventDefault();
        console.log('[EditPhotoModal] handleSubmit called. selectedPhotoFile:', selectedPhotoFile); 

    if (!selectedPhotoFile) {
      alert("Please select a new photo to upload.");
      return;
    }
    onSave(selectedPhotoFile);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-75 z-50 flex justify-center items-center p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md animate-modalFadeIn">
       <style jsx global>{` @keyframes modalFadeIn { to { opacity: 1; transform: scale(1); } } .animate-modalFadeIn { animation: modalFadeIn 0.2s ease-out forwards; opacity:0; transform: scale(0.95); } `}</style>
        <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-semibold text-gray-800">Update Profile Photo</h2>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600 text-2xl leading-none p-1">&times;</button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          {currentPhotoUrl && <img src={currentPhotoUrl} alt="Current profile" className="mx-auto h-32 w-32 rounded-full object-cover mb-4 shadow-md" />}
          <div>
            <label htmlFor="profilePhotoFileModalInput" className="block text-sm font-medium text-gray-700">New Photo (JPG, PNG, GIF, WebP)</label>
            <input 
              type="file" 
              name="profilePhotoFileModalInput" 
              id="profilePhotoFileModalInput"   
              accept="image/*" 
              onChange={(e) => setSelectedPhotoFile(e.target.files[0])} 
              required 
              className="mt-1 file-input-style w-full" 
            />
          </div>
          {selectedPhotoFile && <p className="text-xs text-gray-500 mt-1">Selected: {selectedPhotoFile.name}</p>}
          <div className="flex justify-end space-x-3 pt-2">
            <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
            <button type="submit" disabled={isLoading} className="btn-primary disabled:opacity-70">
              {isLoading ? 'Uploading...' : 'Upload Photo'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
// --- End Edit Modals ---

const HomePage = () => {
  const { currentUser, isAdmin, isLoadingAuth } = useAuth(); 
  const [siteSettings, setSiteSettings] = useState(null);
  const [isLoadingPageData, setIsLoadingPageData] = useState(true); 
  const [errorPageData, setErrorPageData] = useState('');
  const [actionMessage, setActionMessage] = useState('');

  const [showEditIntroModal, setShowEditIntroModal] = useState(false);
  const [showEditPhotoModal, setShowEditPhotoModal] = useState(false);

  const location = useLocation(); 
  const navigate = useNavigate();

  const defaultProfileImageUrl = "/images/default-profile.png"; // Ensure this image exists in your `frontend/public/images/` folder

  const fetchSiteSettings = async () => {
    setIsLoadingPageData(true);
    try {
      const { data } = await api.get('/settings');
      setSiteSettings(data);
    } catch (err) {
      console.error("Error fetching site settings:", err);
      setErrorPageData(err.response?.data?.message || "Could not load site settings.");
      setSiteSettings({ 
        ownerName: '[Your Name]', jobTitle: '[Your Title]', specialization: '[Your Specialization]', 
        homePageIntroParagraph: 'Welcome! Content is loading or not yet configured.',
        profilePhotoUrl: defaultProfileImageUrl
      });
    } finally {
      setIsLoadingPageData(false);
    }
  };

  useEffect(() => {
    if (!isLoadingAuth) { 
      fetchSiteSettings();
    }
  }, [isLoadingAuth]); 

  const handleUpdateTextSettings = async (formData) => { /* ... same as before ... */ };
  const handleProfilePhotoUpload = async (photoFile) => { /* ... same as before ... */ };
  const displayMessage = (message, type = 'success') => { /* ... same as before ... */ };
  // Ensure these functions are fully defined as in the previous complete HomePage.js I sent

  const handleEditClick = (actionCallbackIfAdmin) => {
    if (currentUser && isAdmin) { 
      actionCallbackIfAdmin(); 
    } else {
      navigate('/login', {
        state: {
          message: "To edit this content, please log in as an administrator.",
          from: location 
        }
      });
    }
  };

  if (isLoadingAuth || (isLoadingPageData && !siteSettings)) {
    return <div className="text-center py-20 text-lg text-gray-500">Loading Page Content...</div>;
  }

  const settings = siteSettings || { 
    ownerName: 'Your Name', jobTitle: 'Your Title', specialization: 'Your Specialization', 
    homePageIntroParagraph: 'Content is loading or not yet configured.', profilePhotoUrl: defaultProfileImageUrl 
  };

  if (errorPageData && settings.ownerName === 'Your Name' && (!siteSettings?.ownerName || !siteSettings.ownerName.includes('Your Name'))) { 
    return <div className="text-center py-20 text-red-500 bg-red-100 p-4 rounded-md">{errorPageData}</div>;
  }

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-20">
      {actionMessage && (
        <div className={`p-4 mb-6 rounded-md text-sm text-center ${actionMessage.type === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`} role="alert">
          {actionMessage.text}
        </div>
      )}
      <div className="flex flex-col md:flex-row items-center justify-center md:justify-between gap-8 md:gap-12">
        {/* Text Content Section */}
        <div className="md:w-3/5 lg:w-1/2 text-center md:text-left">
          <div className="relative group"> {/* Added group here for hover effect if needed, button is always visible */}
            <h1 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold text-gray-800 leading-tight mb-4">
              Hello, I'm <span className="text-indigo-600">{settings.ownerName}!</span>
            </h1>
            <p className="text-lg sm:text-xl lg:text-2xl text-gray-600 mb-8 leading-relaxed">
              A passionate {settings.jobTitle} specializing in {settings.specialization}.
            </p>
            {/* Edit button for text - Always in DOM, onClick handles logic */}
            <button 
              onClick={() => handleEditClick(() => setShowEditIntroModal(true))}
              className="absolute top-0 right-0 -mt-1 -mr-1 p-2 bg-gray-100 hover:bg-gray-200 rounded-full text-gray-700 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all"
              title="Edit Text Content"
              aria-label="Edit introductory text"
            >
              ✏️
            </button>
          </div>
          <div className="mt-8 flex flex-col sm:flex-row justify-center md:justify-start gap-4">
            <Link to="/projects" className="btn-primary text-lg px-8 py-3">View My Work</Link>
            <Link to="/contact" className="btn-secondary text-lg px-8 py-3">Get In Touch</Link>
          </div>
        </div>

        {/* Image Section */}
        <div className="md:w-2/5 lg:w-1/2 flex justify-center md:justify-end mt-8 md:mt-0">
          <div className="relative group w-64 h-64 sm:w-80 sm:h-80 lg:w-96 lg:h-96 rounded-full overflow-hidden shadow-2xl border-4 border-white">
            <img 
              src={settings.profilePhotoUrl} 
              alt={settings.ownerName} 
              className="w-full h-full object-cover" 
              onError={(e) => { 
                if (e.target.src !== defaultProfileImageUrl) {
                  e.target.onerror = null; 
                  e.target.src = defaultProfileImageUrl; 
                }
              }}
            />
            {/* Edit button for photo - Always in DOM, onClick handles logic */}
            <button 
              onClick={() => handleEditClick(() => setShowEditPhotoModal(true))}
className="absolute top-2 right-2 z-10 p-2 bg-black bg-opacity-50 hover:bg-opacity-75 text-white rounded-full opacity-0 group-hover:opacity-100 transition-all duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-indigo-400"              title="Change Profile Photo"
              aria-label="Change profile photo"
            >
              ✏️
            </button>
          </div>
        </div>
      </div>

      {/* Introductory Paragraph Section */}
      <section className="my-12 md:my-20 w-full max-w-3xl mx-auto px-4">
         <div className="relative group bg-white p-6 sm:p-8 rounded-lg shadow-lg">
            <h2 className="text-2xl sm:text-3xl font-semibold text-gray-700 mb-6 text-center md:text-left">
              A Little About Me
            </h2>
            <p className="text-md sm:text-lg text-gray-600 leading-relaxed">
              {settings.homePageIntroParagraph}
            </p>
            {/* Edit button for intro paragraph - Always in DOM, onClick handles logic */}
            <button 
              onClick={() => handleEditClick(() => setShowEditIntroModal(true))} 
                className="absolute top-2 right-2 z-30 p-3 bg-red-600 text-white rounded-full shadow-xl border-2 border-white text-lg" // Always visible, red, z-index
    title="Change Profile Photo"
    aria-label="Change profile photo"
  >
    ✏️
            </button>
          </div>
      </section>

      {/* Modals for Editing - Their visibility is controlled by showEdit...Modal state variables */}
      <EditTextModal 
        show={showEditIntroModal} 
        onClose={() => setShowEditIntroModal(false)} 
        currentSettings={settings} 
        onSave={handleUpdateTextSettings}
        isLoading={isLoadingPageData}
      />
      <EditPhotoModal 
        show={showEditPhotoModal} 
        onClose={() => setShowEditPhotoModal(false)}
        currentPhotoUrl={settings.profilePhotoUrl}
        onSave={handleProfilePhotoUpload}
        isLoading={isLoadingPageData}
      />
    </div>
  );
};

export default HomePage;
