// portfolio_py/frontend/src/pages/admin/AboutPage/AchievementsSection.js
import React from 'react';
import { useAuth } from '../../../contexts/AuthContext'; // Adjusted path

const AchievementsSection = ({ achievementsData }) => {
  const { isAdmin } = useAuth();

  return (
    <section id="achievements" className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-xl transition-colors duration-300">
      <div className="flex flex-col sm:flex-row justify-between items-center mb-8">
        <h2 className="text-3xl font-semibold text-gray-800 dark:text-gray-100 mb-4 sm:mb-0">
          Achievements
        </h2>
        {isAdmin && (
          <button 
            onClick={() => alert('TODO: Implement Add Achievement functionality')} 
            className="btn-primary text-sm py-2 px-4"
          >
            + Add Achievement
          </button>
        )}
      </div>

      {(!achievementsData || achievementsData.length === 0) && !isAdmin && (
        <p className="text-gray-500 dark:text-gray-400">No achievements to display at the moment.</p>
      )}
      {(!achievementsData || achievementsData.length === 0) && isAdmin && (
        <p className="text-gray-500 dark:text-gray-400">No achievements listed yet. Click "Add Achievement" to add one.</p>
      )}

      {achievementsData && achievementsData.length > 0 && (
        <ul className="list-disc list-inside space-y-3 text-gray-700 dark:text-gray-300">
          {achievementsData.map((achievement, index) => (
            <li key={index} className="relative group pl-2">
              {achievement}
              {isAdmin && (
                <div className="absolute top-0 right-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300 inline-flex ml-2">
                  <button 
                    onClick={() => alert(`TODO: Implement Edit for achievement ${index + 1}`)} 
                    className="text-xs bg-yellow-400 hover:bg-yellow-500 text-gray-800 font-semibold py-0.5 px-1.5 rounded mr-1"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => alert(`TODO: Implement Delete for achievement ${index + 1}`)} 
                    className="text-xs bg-red-500 hover:bg-red-600 text-white font-semibold py-0.5 px-1.5 rounded"
                  >
                    Del
                  </button>
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
      {isAdmin && (
          <p className="mt-6 text-xs text-gray-400 dark:text-gray-500 italic">
            Note: To make achievements fully manageable, backend API endpoints and services would need to be implemented.
          </p>
        )}
    </section>
  );
};

export default AchievementsSection;
