// frontend/src/components/common/Footer.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSettings } from '../../contexts/SettingsContext';
import * as socialLinkService from '../../services/socialLinkService';
import { iconComponents } from '../../pages/admin/AdminSocialLinksPage'; // Reuse the icon map

const Footer = () => {
  const { siteSettings } = useSettings();
  const [socialLinks, setSocialLinks] = useState([]);
  const currentYear = new Date().getFullYear();

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const data = await socialLinkService.getPublicSocialLinks();
        setSocialLinks(data || []);
      } catch (error) {
        console.error("Failed to fetch social links for footer:", error);
      }
    };
    fetchLinks();
  }, []);

  return (
    <footer className="bg-gray-800 text-gray-400 dark:bg-slate-900">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col md:flex-row justify-between items-center space-y-6 md:space-y-0">

          {/* Left Side: Name and Copyright */}
          <div className="text-center md:text-left">
            <h3 className="text-lg font-semibold text-white">{siteSettings.ownerName || 'Your Name'}</h3>
            <p className="text-sm">
              &copy; {currentYear} All Rights Reserved.
            </p>
          </div>

          {/* Center: Quick Links */}
          <nav className="flex flex-wrap justify-center gap-x-6 gap-y-2">
            <Link to="/about" className="hover:text-white transition-colors">About</Link>
            <Link to="/projects" className="hover:text-white transition-colors">Projects</Link>
            <Link to="/resume" className="hover:text-white transition-colors">Resume</Link>
            <Link to="/contact" className="hover:text-white transition-colors">Contact</Link>
          </nav>

          {/* Right Side: Social Icons */}
          {socialLinks.length > 0 && (
            <div className="flex justify-center space-x-4">
              {socialLinks.map((link) => {
                const IconComponent = iconComponents[link.iconName.toLowerCase()] || iconComponents.default;
                return (
                  <a
                    key={link._id}
                    href={link.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    aria-label={link.name}
                    className="text-gray-400 hover:text-white transition-transform hover:scale-110"
                  >
                    <IconComponent size={22} />
                  </a>
                );
              })}
            </div>
          )}
        </div>
        <div className="mt-8 pt-4 border-t border-gray-700 text-center text-xs">
          <p>Built with React, Node.js, and Tailwind CSS.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
