// frontend/src/components/admin/AdminLayout.js
import React from 'react';
import { NavLink, Outlet, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext'; // Adjust path as needed

const AdminLayout = () => {
  const { currentUser, logout, isAdmin } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    // navigate('/login'); // logout() in AuthContext already handles navigation to /login
  };

  // This layout will only be rendered if ProtectedRoute allows it,
  // but we can add an extra check here for isAdmin if desired, or rely on ProtectedRoute.
  // For now, ProtectedRoute will handle the main access control.

  const navLinkClasses = ({ isActive }) =>
    `block px-4 py-2.5 rounded-md text-sm font-medium transition-colors duration-150 ease-in-out ${
      isActive
        ? 'bg-indigo-700 text-white shadow-md'
        : 'text-gray-200 hover:bg-indigo-500 hover:text-white'
    }`;

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 text-gray-100 flex flex-col space-y-2 p-4 shadow-lg">
        <div className="text-center py-4 border-b border-gray-700">
          <h2 className="text-xl font-semibold text-white">Admin Portal</h2>
          {currentUser && (
            <p className="text-xs text-gray-400 mt-1">Welcome, {currentUser.name}</p>
          )}
        </div>
        <nav className="flex-grow">
          <NavLink to="/admin/dashboard" className={navLinkClasses}>
            {/* Icon placeholder */} Dashboard
          </NavLink>
          <NavLink to="/admin/resumes" className={navLinkClasses}>
            {/* Icon placeholder */} Manage Resumes
          </NavLink>
          <NavLink to="/admin/projects" className={navLinkClasses}>
            {/* Icon placeholder */} Manage Projects
          </NavLink>
          {/* Add more admin links here as needed */}
        </nav>
        <div className="pt-4 border-t border-gray-700">
          <NavLink to="/" className={`${navLinkClasses({isActive:false})} mb-2 text-center bg-gray-700 hover:bg-gray-600`}>
             Go to Public Site
          </NavLink>
          <button
            onClick={handleLogout}
            className="w-full block px-4 py-2.5 rounded-md text-sm font-medium bg-red-600 hover:bg-red-700 text-white shadow-md transition-colors duration-150 ease-in-out"
          >
            {/* Icon placeholder */} Logout
          </button>
        </div>
      </aside>

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Optional: Admin Header Bar (if needed, separate from sidebar) */}
        {/* <header className="bg-white shadow p-4">
          <h1 className="text-xl font-semibold text-gray-700">Current Section Title</h1>
        </header> */}

        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100 p-6 md:p-8">
          <Outlet /> {/* Child routes (admin pages) will render here */}
        </main>
      </div>
    </div>
  );
};

export default AdminLayout;
