// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';
import { FaPen } from 'react-icons/fa';

const EditTextModal = ({ show, onClose, currentSettings, onSave, isLoading }) => {
  const [formData, setFormData] = useState({ ownerName: '', jobTitle: '', specialization: '', homePageIntroParagraph: '' });

  useEffect(() => {
    if (show && currentSettings) {
      setFormData({
        ownerName: currentSettings.ownerName || '',
        jobTitle: currentSettings.jobTitle || '',
        specialization: currentSettings.specialization || '',
        homePageIntroParagraph: currentSettings.homePageIntroParagraph || '',
      });
    }
  }, [show, currentSettings]);

  if (!show) return null;

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleSubmit = (e) => { e.preventDefault(); onSave(formData); };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-lg animate-fade">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">Edit Homepage Content</h2>
          <button onClick={onClose} className="text-2xl">&times;</button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          {['ownerName', 'jobTitle', 'specialization'].map((field) => (
            <div key={field}>
              <label className="block text-sm font-medium capitalize">{field}</label>
              <input type="text" name={field} value={formData[field]} onChange={handleChange} className="input-style w-full" />
            </div>
          ))}
          <div>
            <label className="block text-sm font-medium">Intro Paragraph</label>
            <textarea name="homePageIntroParagraph" rows="4" value={formData.homePageIntroParagraph} onChange={handleChange} className="input-style w-full"></textarea>
          </div>
          <div className="flex justify-end space-x-3">
            <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
            <button type="submit" disabled={isLoading} className="btn-primary">{isLoading ? 'Saving...' : 'Save'}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

const EditPhotoModal = ({ show, onClose, currentPhotoUrl, onSave, isLoading }) => {
  const [selectedPhotoFile, setSelectedPhotoFile] = useState(null);
  useEffect(() => { if (!show) setSelectedPhotoFile(null); }, [show]);
  if (!show) return null;
  const handleSubmit = (e) => { e.preventDefault(); if (!selectedPhotoFile) return alert('Choose photo.'); onSave(selectedPhotoFile); };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md animate-fade">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">Update Profile Photo</h2>
          <button onClick={onClose} className="text-2xl">&times;</button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          {currentPhotoUrl && <img src={currentPhotoUrl} alt="Profile" className="mx-auto h-32 w-32 rounded-full object-cover shadow" />}
          <input type="file" accept="image/*" onChange={(e) => setSelectedPhotoFile(e.target.files[0])} className="file-input w-full" required />
          <div className="flex justify-end space-x-3">
            <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
            <button type="submit" className="btn-primary">{isLoading ? 'Uploading...' : 'Upload'}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

const HomePage = () => {
  const { currentUser, isAdmin, isLoadingAuth } = useAuth();
  const [siteSettings, setSiteSettings] = useState(null);
  const [isLoadingPageData, setIsLoadingPageData] = useState(true);
  const [actionMessage, setActionMessage] = useState('');
  const [showEditIntroModal, setShowEditIntroModal] = useState(false);
  const [showEditPhotoModal, setShowEditPhotoModal] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const defaultProfileImageUrl = "/images/default-profile.png";

  useEffect(() => { if (!isLoadingAuth) fetchSiteSettings(); }, [isLoadingAuth]);

  const fetchSiteSettings = async () => {
    setIsLoadingPageData(true);
    try {
      const { data } = await api.get('/settings');
      setSiteSettings(data);
    } catch (err) {
      setSiteSettings({ ownerName: 'Your Name', jobTitle: 'Your Title', specialization: 'Your Specialization', homePageIntroParagraph: 'Default intro.', profilePhotoUrl: defaultProfileImageUrl });
    } finally { setIsLoadingPageData(false); }
  };

  const handleEditClick = (setModalOpen) => {
    if (currentUser && isAdmin) setModalOpen(true);
    else navigate('/login', { state: { message: "Admin login required.", from: location } });
  };

  const handleUpdateTextSettings = async (formData) => {
    setIsLoadingPageData(true);
    try {
      const { data } = await api.put('/settings', formData);
      setSiteSettings(data);
      setShowEditIntroModal(false);
    } finally { setIsLoadingPageData(false); }
  };

  const handleProfilePhotoUpload = async (photoFile) => {
    const formData = new FormData();
    formData.append('profilePhoto', photoFile);
    setIsLoadingPageData(true);
    try {
      const { data } = await api.post('/settings/profile-photo', formData);
      setSiteSettings(data.settings);
      setShowEditPhotoModal(false);
    } finally { setIsLoadingPageData(false); }
  };

  if (isLoadingAuth || (isLoadingPageData && !siteSettings)) return <div className="text-center py-20 text-lg text-gray-500">Loading...</div>;

  const settings = siteSettings || { ownerName: 'Your Name', jobTitle: 'Title', specialization: 'Specialization', homePageIntroParagraph: 'Intro.', profilePhotoUrl: defaultProfileImageUrl };
  const profilePhotoToDisplay = settings.profilePhotoUrl && settings.profilePhotoUrl !== '/images/default-profile.png'
    ? `${process.env.REACT_APP_API_URL_WITHOUT_API_PREFIX || 'http://localhost:5001'}${settings.profilePhotoUrl}`
    : defaultProfileImageUrl;

  return (
    <div className="min-h-screen bg-gradient-to-br from-white via-gray-50 to-indigo-50 px-4 py-12">
      {actionMessage && <div className="text-center py-4 text-sm text-white bg-green-500">{actionMessage}</div>}

      <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between gap-8 bg-white p-8 rounded-3xl shadow-xl">
        <div className="md:w-1/2">
          <h1 className="text-4xl sm:text-5xl font-bold text-gray-800 mb-4">Hi, I'm <span className="text-indigo-600">{settings.ownerName}</span></h1>
          <p className="text-lg text-gray-600 mb-6">A {settings.jobTitle} specializing in {settings.specialization}.</p>
          <button onClick={() => handleEditClick(setShowEditIntroModal)} className="inline-flex items-center gap-2 text-sm text-indigo-600 hover:underline mb-4">
            <FaPen /> Edit Intro
          </button>
          <div className="flex gap-4">
            <Link to="/projects" className="btn-primary">View My Work</Link>
            <Link to="/contact" className="btn-secondary">Contact</Link>
          </div>
        </div>
        <div className="relative md:w-1/2 flex justify-center">
          <div className="w-64 h-64 sm:w-80 sm:h-80 lg:w-96 lg:h-96 rounded-full overflow-visible shadow-2xl border-4 border-white relative">
            <img src={profilePhotoToDisplay} alt={settings.ownerName} className="w-full h-full object-cover rounded-full" onError={(e) => { e.target.src = defaultProfileImageUrl; }} />
            <button onClick={() => handleEditClick(setShowEditPhotoModal)} className="absolute bottom-4 right-4 p-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-full shadow-md">
              <FaPen />
            </button>
          </div>
        </div>
      </div>

      <section className="mt-12 max-w-3xl mx-auto bg-white p-8 rounded-2xl shadow">
        <div className="relative">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">About Me</h2>
          <p className="text-gray-600 text-lg leading-relaxed">{settings.homePageIntroParagraph}</p>
          <button onClick={() => handleEditClick(setShowEditIntroModal)} className="absolute top-0 right-0 p-2 text-gray-600 hover:text-indigo-600">
            <FaPen />
          </button>
        </div>
      </section>

      <EditTextModal show={showEditIntroModal} onClose={() => setShowEditIntroModal(false)} currentSettings={settings} onSave={handleUpdateTextSettings} isLoading={isLoadingPageData} />
      <EditPhotoModal show={showEditPhotoModal} onClose={() => setShowEditPhotoModal(false)} currentPhotoUrl={profilePhotoToDisplay} onSave={handleProfilePhotoUpload} isLoading={isLoadingPageData} />
    </div>
  );
};

export default HomePage;
