// frontend/src/components/common/Navbar.js
import React, { useState } from 'react';
import { NavLink } from 'react-router-dom'; // Use NavLink for active styling
import { useAuth } from '../../contexts/AuthContext'; // Adjust path if needed
const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const { currentUser, logout } = useAuth(); 

  const navLinkClasses = ({ isActive }) =>
    `px-3 py-2 rounded-md text-sm font-medium ${
      isActive
        ? 'bg-gray-900 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    } md:ml-4`;

  const mobileNavLinkClasses = ({ isActive }) =>
    `block px-3 py-2 rounded-md text-base font-medium ${
      isActive
        ? 'bg-gray-900 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;


 return (
  <nav className="bg-gray-800 shadow-lg">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <div className="flex items-center justify-between h-16">
        {/* ... (Your Name link) ... */}
        <div className="hidden md:block">
          <div className="ml-10 flex items-baseline space-x-4">
            <NavLink to="/" className={navLinkClasses}>Home</NavLink>
            <NavLink to="/about" className={navLinkClasses}>About</NavLink>
            <NavLink to="/projects" className={navLinkClasses}>Projects</NavLink>
            <NavLink to="/resume" className={navLinkClasses}>Resume</NavLink>
            <NavLink to="/contact" className={navLinkClasses}>Contact</NavLink>
            {currentUser ? (
              <button
                onClick={logout}
                className="px-3 py-2 rounded-md text-sm font-medium text-gray-300 hover:bg-gray-700 hover:text-white"
              >
                Logout
              </button>
            ) : (
              <NavLink to="/login" className={navLinkClasses}>
                Login
              </NavLink>
            )}
          </div>
        </div>
      </div>
    </div>

    {/* Mobile menu */}
    {isOpen && (
      <div className="md:hidden" id="mobile-menu">
        <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
          <NavLink to="/" className={mobileNavLinkClasses} onClick={() => setIsOpen(false)}>Home</NavLink>
          {/* ... other mobile links ... */}
          <NavLink to="/contact" className={mobileNavLinkClasses} onClick={() => setIsOpen(false)}>Contact</NavLink>
          {currentUser ? (
            <button
              onClick={() => { logout(); setIsOpen(false); }}
              className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-300 hover:bg-gray-700 hover:text-white"
            >
              Logout
            </button>
          ) : (
            <NavLink to="/login" className={mobileNavLinkClasses} onClick={() => setIsOpen(false)}>
              Login
            </NavLink>
          )}
        </div>
      </div>
    )}
  </nav>
)};
