// frontend/src/pages/ResumePage.js
import React, { useState, useEffect } from 'react';

// We'll keep the mockResumes array mutable for this simulation
let mockResumesData = [
  { _id: '1', field: 'Machine Learning', filename: 'ML_Resume_Example.pdf', url: '/Harisharan_ML_Resume.pdf', uploadDate: new Date().toISOString() },
  { _id: '2', field: 'Web Development', filename: 'WebDev_Resume_Example.pdf', url: '/Harisharan_WebDev_Resume.pdf', uploadDate: new Date().toISOString() },
];

const getResumes = async () => {
  console.log("Fetching resumes (mocked for ResumePage)");
  // Return a new copy of the array so React detects a change
  return new Promise(resolve => setTimeout(() => resolve({ data: [...mockResumesData] }), 300));
};

const apiAddResume = async (newResumeData) => {
  console.log("Adding new resume (mocked for ResumePage):", newResumeData);
  return new Promise(resolve => setTimeout(() => {
    const newResume = {
      _id: String(Date.now()), 
      ...newResumeData,
      url: `/mock_${newResumeData.filename || 'new_resume.pdf'}`, 
      uploadDate: new Date().toISOString(),
    };
    mockResumesData.push(newResume);
    resolve({ data: newResume });
  }, 300));
};

const apiDeleteResume = async (id) => {
  console.log(`Deleting resume with id: ${id} (mocked for ResumePage)`);
  return new Promise(resolve => setTimeout(() => {
    mockResumesData = mockResumesData.filter(r => r._id !== id);
    resolve({ data: { message: 'Resume deleted successfully (mocked)' } });
  }, 300));
};

const ResumePage = () => {
  const [resumes, setResumes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newResumeField, setNewResumeField] = useState('');
  const [newResumeFilename, setNewResumeFilename] = useState('');
  const [selectedFile, setSelectedFile] = useState(null); 

  const fetchResumes = async () => {
    setIsLoading(true);
    try {
      const response = await getResumes();
      setResumes(response.data);
      setError(null);
    } catch (err) {
      console.error("Error fetching resumes:", err); 
      setError("Failed to load resumes.");
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchResumes();
  }, []);

  const handleDelete = async (resumeId, resumeField) => {
    if (window.confirm(`Are you sure you want to delete the ${resumeField} resume?`)) {
      setIsLoading(true);
      try {
        await apiDeleteResume(resumeId);
        fetchResumes(); 
      } catch (err) {
        console.error("Error deleting resume (mocked):", err);
        alert('Failed to delete resume (mocked).');
      } finally {
        // Ensure loading is set to false even if fetchResumes has an issue in this context
        // or if confirmation is cancelled after setIsLoading(true) - though confirm blocks.
        // However, fetchResumes already handles its own setIsLoading(false).
        // The main concern is if apiDeleteResume throws an error *before* fetchResumes is called.
        // In that case, the catch block handles setIsLoading(false).
        // If apiDeleteResume succeeds but then fetchResumes call fails,
        // fetchResumes' finally block will set it. So this should be okay.
        // For safety, you could add one here, but it might be redundant if fetchResumes always runs.
        setIsLoading(false); 
      }
    }
  };

  const handleUpdate = (resume) => {
    alert(`Update functionality for "${resume.field}" resume (ID: ${resume._id}) would go here.`);
  };
// ... within ResumePage component ...
const handleAddNewResume = async (e) => {
  e.preventDefault();
  if (!newResumeField.trim() || !newResumeFilename.trim()) { // Changed to also check newResumeFilename
    alert("Please provide a field/category and a filename.");
    return;
  }
  if (!selectedFile) { // <--- ADD THIS CHECK
    alert("Please select a PDF file to upload.");
    return;
  }

  setIsLoading(true);

  // THIS PART WILL CHANGE SIGNIFICANTLY WHEN WE CONNECT TO THE REAL BACKEND
  // For now, the mock just uses the filename and field.
  // Later, we will construct FormData here.
  const resumeDataForMock = { 
    field: newResumeField,
    filename: selectedFile.name, // Use the actual filename from the selected file for the mock
  };

  try {
    // This still calls the mock API for now
    await apiAddResume(resumeDataForMock); 
    setNewResumeField('');
    setNewResumeFilename(''); // We might not need this input anymore if we use selectedFile.name
    setSelectedFile(null);   // <--- CLEAR THE SELECTED FILE
    // Also clear the visual file input (browsers often do this on their own after form submission,
    // but for SPA it might need a little help if the form isn't fully reset, e.g. e.target.reset() on the form)
    if (e.target.resumeFile) e.target.resumeFile.value = null; // Reset file input visually

    setShowAddForm(false);
    fetchResumes(); 
  } catch (err) {
    console.error("Error adding resume (mocked):", err);
    alert("Failed to add resume (mocked).");
  } finally {
    setIsLoading(false);
  }
};

  if (isLoading && resumes.length === 0) { 
    return <div className="text-center py-10">Loading resumes...</div>;
  }

  if (error) {
    return <div className="text-center py-10 text-red-500 bg-red-100 p-3 rounded">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex flex-col sm:flex-row justify-between items-center mb-8 gap-4">
        <h1 className="text-3xl md:text-4xl font-bold text-gray-800 text-center sm:text-left">My Resumes</h1>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          className="w-full sm:w-auto bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-6 rounded-lg shadow-md hover:shadow-lg transition duration-300"
        >
          {showAddForm ? 'Cancel Add' : 'Add New Resume Field'}
        </button>
      </div>

      {showAddForm && (
        <div className="bg-white shadow-xl rounded-lg p-6 md:p-8 mb-10 border border-gray-200">
          <h2 className="text-2xl font-semibold text-gray-700 mb-6">Add New Resume Field</h2>
          <form onSubmit={handleAddNewResume} className="space-y-6">
            <div>
              <label htmlFor="newResumeField" className="block text-sm font-medium text-gray-700 mb-1">
                Field / Category Name (e.g., Python Developer) <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                id="newResumeField"
                value={newResumeField}
                onChange={(e) => setNewResumeField(e.target.value)}
                required
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                placeholder="e.g., Data Scientist"
              />
            </div>
            <div>
              <label htmlFor="newResumeFilename" className="block text-sm font-medium text-gray-700 mb-1">
                Resume Filename (e.g., my_ds_resume.pdf) <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                id="newResumeFilename"
                value={newResumeFilename}
                onChange={(e) => setNewResumeFilename(e.target.value)}
                required
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                placeholder="e.g., Harisharan_Python_Resume.pdf"
              />
            </div>
            <div>
  <label htmlFor="resumeFile" className="block text-sm font-medium text-gray-700 mb-1">
    Upload PDF File: <span className="text-red-500">*</span>
  </label>
  <input
    type="file"
    id="resumeFile" // Added id for the label
    accept=".pdf"
    // REMOVED 'disabled' attribute
    onChange={(e) => setSelectedFile(e.target.files[0])} // ADD THIS onChange HANDLER
    required // Make file selection required
    className="mt-1 block w-full text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-indigo-50 file:text-indigo-700 hover:file:bg-indigo-100 cursor-pointer" // Updated className
  />
</div>
            <button
              type="submit"
              disabled={isLoading}
              className="w-full sm:w-auto bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-6 rounded-lg shadow-md hover:shadow-lg transition duration-300 disabled:opacity-70"
            >
              {isLoading ? 'Adding...' : 'Add This Resume Field (Simulated)'}
            </button>
          </form>
        </div>
      )}

      {resumes.length === 0 && !isLoading && !showAddForm && (
        <p className="text-center text-gray-500 py-10">No resumes found. Click "Add New Resume Field" to get started!</p>
      )}

      {isLoading && resumes.length > 0 && <div className="text-center py-5 text-sm text-gray-500">Updating list...</div>}

      <div className="space-y-6">
        {resumes.map((resume) => (
          <div key={resume._id} className="bg-white shadow-lg rounded-lg p-6 flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 hover:shadow-xl transition-shadow duration-300">
            <div className="flex-grow">
              <h2 className="text-xl font-semibold text-gray-700">{resume.field}</h2>
              <p className="text-sm text-gray-500">Filename: {resume.filename}</p>
              <p className="text-sm text-gray-500">Uploaded: {new Date(resume.uploadDate).toLocaleDateString()}</p>
            </div>
            <div className="mt-4 sm:mt-0 flex flex-wrap flex-col sm:flex-row gap-3 items-stretch sm:items-center w-full sm:w-auto">
              <a
                href={resume.url} 
                target="_blank"
                rel="noopener noreferrer"
                download={resume.filename}
                className="w-full sm:w-auto text-center bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-md text-sm shadow transition duration-300"
              >
                Download
              </a>
              <button
                onClick={() => handleUpdate(resume)}
                className="w-full sm:w-auto bg-yellow-400 hover:bg-yellow-500 text-gray-800 font-medium py-2 px-4 rounded-md text-sm shadow transition duration-300"
              >
                Update
              </button>
              <button
                onClick={() => handleDelete(resume._id, resume.field)}
                disabled={isLoading}
                className="w-full sm:w-auto bg-red-500 hover:bg-red-600 text-white font-medium py-2 px-4 rounded-md text-sm shadow transition duration-300 disabled:opacity-70"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ResumePage;
