// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';
import { FaRegEdit } from 'react-icons/fa';

const HomePage = () => {
  const { currentUser, isAdmin, isLoadingAuth } = useAuth();
  const [settings, setSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({ ownerName: '', jobTitle: '', specialization: '', homePageIntroParagraph: '' });
  const [profilePhoto, setProfilePhoto] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isLoadingAuth) fetchSettings();
  }, [isLoadingAuth]);

  const fetchSettings = async () => {
    try {
      const { data } = await api.get('/settings');
      setSettings(data);
      setFormData({
        ownerName: data.ownerName || '',
        jobTitle: data.jobTitle || '',
        specialization: data.specialization || '',
        homePageIntroParagraph: data.homePageIntroParagraph || '',
      });
    } catch (err) {
      setSettings({});
    } finally {
      setLoading(false);
    }
  };

  const handleEditClick = () => {
    if (currentUser && isAdmin) setEditing(true);
    else navigate('/login', { state: { message: "Admin login required.", from: location } });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handlePhotoChange = (e) => {
    setProfilePhoto(e.target.files[0]);
  };

  const handleSave = async () => {
    try {
      await api.put('/settings', formData);
      if (profilePhoto) {
        const photoForm = new FormData();
        photoForm.append('profilePhoto', profilePhoto);
        await api.post('/settings/profile-photo', photoForm);
      }
      fetchSettings();
      setEditing(false);
    } catch (err) {
      console.error(err);
    }
  };

  if (isLoadingAuth || loading) return <div className="text-center py-20">Loading...</div>;

  const photoUrl = settings?.profilePhotoUrl
    ? `${process.env.REACT_APP_API_URL_WITHOUT_API_PREFIX || 'http://localhost:5001'}${settings.profilePhotoUrl}`
    : '/images/default-profile.png';

  return (
    <div className="min-h-screen bg-gradient-to-br from-white via-gray-50 to-indigo-100 p-8">
      <div className="max-w-5xl mx-auto bg-white p-8 rounded-xl shadow-lg flex flex-col md:flex-row gap-8">
        <div className="flex-1">
          <h1 className="text-4xl font-bold mb-2 text-indigo-700">Hi, I'm {settings.ownerName}</h1>
          <p className="text-lg text-gray-600 mb-4">
            A {settings.jobTitle} specializing in {settings.specialization}.
          </p>
          <p className="text-gray-700 mb-4">{settings.homePageIntroParagraph}</p>
          <div className="flex gap-4">
            <Link to="/projects" className="btn-primary">Projects</Link>
            <Link to="/contact" className="btn-secondary">Contact</Link>
          </div>
          <button onClick={handleEditClick} className="mt-4 inline-flex items-center text-sm text-indigo-600 hover:underline">
            <FaRegEdit className="mr-1" /> Edit
          </button>
        </div>
        <div className="flex justify-center">
          <img src={photoUrl} alt="Profile" className="w-64 h-64 rounded-full object-cover border-4 border-white shadow-xl" onError={(e) => { e.target.src = '/images/default-profile.png'; }} />
        </div>
      </div>

      {editing && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4">
          <div className="bg-white p-6 rounded-lg w-full max-w-xl space-y-4">
            <h2 className="text-xl font-semibold mb-2">Edit Homepage</h2>
            {['ownerName', 'jobTitle', 'specialization'].map((field) => (
              <div key={field}>
                <label className="block text-sm font-medium capitalize">{field}</label>
                <input
                  type="text"
                  name={field}
                  value={formData[field]}
                  onChange={handleInputChange}
                  className="w-full border px-3 py-2 rounded"
                />
              </div>
            ))}
            <div>
              <label className="block text-sm font-medium">Intro Paragraph</label>
              <textarea
                name="homePageIntroParagraph"
                rows="4"
                value={formData.homePageIntroParagraph}
                onChange={handleInputChange}
                className="w-full border px-3 py-2 rounded"
              ></textarea>
            </div>
            <div>
              <label className="block text-sm font-medium">Profile Photo</label>
              <input type="file" accept="image/*" onChange={handlePhotoChange} className="w-full" />
            </div>
            <div className="flex justify-end gap-2">
              <button onClick={() => setEditing(false)} className="btn-secondary">Cancel</button>
              <button onClick={handleSave} className="btn-primary">Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
