// frontend/src/pages/AboutPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext'; // For admin functionalities later
import { getSkills } from '../services/skillService'; // <-- CORRECTED LINE
// or import api from '../services/api'; // If you prefer to call directly

// --- Placeholder Data (Remove or keep for fallback if desired) ---
// const aboutMeData = { ... }; // Keep if you still want it for other sections
// const educationData = [ ... ]; // Keep if you still want it for other sections
// const certificationsData = [ ... ]; // Keep
// const achievementsData = [ ... ]; // Keep

// We will replace this hardcoded skillsData with fetched data
/*
const skillsData = {
  "Languages": ["JavaScript (ES6+)", "Python", "Java", "C++", "HTML5", "CSS3", "SQL"],
  "Frameworks & Libraries": ["React", "Node.js", "Express.js", "Tailwind CSS", "Mongoose"],
  "Databases": ["MongoDB"],
  "Tools & Platforms": ["Git & GitHub", "VS Code", "Postman", "Docker (Basic)"],
  "Concepts": ["RESTful APIs", "Agile Methodologies", "Problem Solving", "Data Structures"]
};
*/
// --- End Placeholder Data ---


const AboutPage = () => {
  const { isAdmin } = useAuth(); // Get admin status

  // State for skills
  const [skills, setSkills] = useState([]); // Will hold the fetched skills
  const [loadingSkills, setLoadingSkills] = useState(true);
  const [errorSkills, setErrorSkills] = useState(null);

  // State for other sections (if you want to make them dynamic later)
  // const [education, setEducation] = useState([]);
  // const [loadingEducation, setLoadingEducation] = useState(true);
  // const [errorEducation, setErrorEducation] = useState(null);

  // Fetch Skills
  useEffect(() => {
    const fetchSkillsData = async () => {
      try {
        setLoadingSkills(true);
        const data = await getSkills(); // Using skillService
        // Or: const response = await api.get('/skills'); const data = response.data;
        setSkills(data);
        setErrorSkills(null);
      } catch (error) {
        setErrorSkills(error.message || 'Failed to fetch skills');
        console.error("Error fetching skills:", error);
      } finally {
        setLoadingSkills(false);
      }
    };

    fetchSkillsData();
  }, []);

  // Helper function to group skills by category from the flat array
  const groupSkillsByCategory = (skillList) => {
    if (!skillList || skillList.length === 0) return {};
    return skillList.reduce((acc, skill) => {
      const category = skill.category || 'Other'; // Default category if undefined
      if (!acc[category]) {
        acc[category] = [];
      }
      acc[category].push(skill);
      return acc;
    }, {});
  };

  const groupedSkills = groupSkillsByCategory(skills);

  // Other sections (About Me intro, Education, Certifications, Achievements)
  // For now, keep their existing static data or structure.
  // We'll make Education dynamic next.

  // About Me data (assuming it's still static for now)
  const aboutMeData = {
    introduction: [
      "Hello! I'm Harisharan...", // Your intro
    ],
    philosophy: "I believe that the best solutions..." // Your philosophy
  };

  const educationData = [ /* Your static education data for now */ ];
  const certificationsData = [ /* Your static certifications data for now */ ];
  const achievementsData = [ /* Your static achievements data for now */ ];


  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-16 space-y-16 md:space-y-20">

      <div className="text-center">
        <h1 className="text-4xl sm:text-5xl font-bold text-gray-900 dark:text-white mb-4 transition-colors duration-300">
          About Me
        </h1>
        <p className="text-lg text-gray-600 dark:text-gray-400 max-w-3xl mx-auto transition-colors duration-300">
          A little about my journey, my passion for development, and what drives me.
        </p>
      </div>

      {/* Who I Am Section (Still using static aboutMeData for this example) */}
      <section id="who-i-am" className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-xl transition-colors duration-300">
        <h2 className="text-3xl font-semibold text-gray-800 dark:text-gray-100 mb-6">Who I Am</h2>
        <div className="space-y-4 text-gray-700 dark:text-gray-300 leading-relaxed text-base sm:text-lg">
          {aboutMeData.introduction.map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
          {aboutMeData.philosophy && (
            <p className="mt-6 italic border-l-4 border-indigo-500 dark:border-indigo-400 pl-4 py-3 bg-indigo-50 dark:bg-slate-700/60 rounded-r-md">
              "{aboutMeData.philosophy}"
            </p>
          )}
        </div>
      </section>

      {/* --- Skills Section (Now Dynamic) --- */}
      <section id="skills">
        <div className="flex justify-between items-center mb-10">
            <h2 className="text-3xl font-bold text-gray-900 dark:text-white text-center sm:text-left flex-grow">Technical Skills</h2>
            {/* "Add New Skill" button will go here for admin later */}
        </div>

        {loadingSkills && (
          <div className="text-center py-10">
            <p className="dark:text-gray-300">Loading skills...</p> {/* Replace with a spinner component if you have one */}
          </div>
        )}
        {errorSkills && (
          <div className="text-center py-10 text-red-500 dark:text-red-400 bg-red-100 dark:bg-red-900/30 p-4 rounded-md">
            <p>Error: {errorSkills}</p>
          </div>
        )}
        {!loadingSkills && !errorSkills && skills.length === 0 && (
            <div className="text-center py-10 text-gray-500 dark:text-gray-400 bg-gray-50 dark:bg-slate-800 p-6 rounded-xl shadow-md">
                <p>No skills have been added yet.</p>
                {/* Admin will see an "Add Skill" button here too */}
            </div>
        )}
        {!loadingSkills && !errorSkills && skills.length > 0 && (
          <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-xl transition-colors duration-300">
            {Object.entries(groupedSkills).map(([category, skillsInCategory]) => (
              <div key={category} className="mb-8 last:mb-0">
                <h3 className="text-xl sm:text-2xl font-semibold text-indigo-600 dark:text-indigo-400 mb-4 border-b-2 border-indigo-200 dark:border-slate-700 pb-2">
                  {category}
                </h3>
                <div className="flex flex-wrap gap-3">
                  {skillsInCategory.map((skill) => (
                    <div key={skill._id} className="relative group"> {/* For edit/delete icons later */}
                        <span 
                          className="bg-indigo-100 dark:bg-slate-700 text-indigo-800 dark:text-indigo-300 px-4 py-2 rounded-full text-sm font-medium shadow-sm"
                        >
                          {skill.name}
                        </span>
                        {/* Edit/Delete buttons will be positioned absolutely within this div for admin later */}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
      {/* --- End Skills Section --- */}


      {/* Education Section (Still static for now) */}
      <section id="education">
        <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-10 text-center">Education</h2>
        <div className="space-y-8">
          {educationData.map((edu, index) => ( // Assuming edu objects have unique enough content for key if no id
            <div 
              key={edu.id || index} 
              className="bg-white dark:bg-slate-800 p-6 rounded-xl shadow-xl transition-all duration-300 hover:shadow-2xl border border-gray-200 dark:border-slate-700 hover:border-indigo-300 dark:hover:border-indigo-500"
            >
              {/* ... education details ... */}
              <h3 className="text-xl font-semibold text-gray-800 dark:text-gray-100">{edu.degree}</h3>
              <p className="text-sm text-indigo-600 dark:text-indigo-400 mt-1 sm:mt-0 whitespace-nowrap">{edu.startDate} - {edu.endDate}</p>
              <p className="text-md font-medium text-gray-700 dark:text-gray-300">{edu.institution}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Certifications Section (Still static) */}
      <section id="certifications">
        {/* ... existing certifications JSX ... */}
      </section>

      {/* Achievements Section (Still static) */}
      <section id="achievements">
        {/* ... existing achievements JSX ... */}
      </section>

    </div>
  );
};

export default AboutPage;
