// frontend/src/pages/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom'; // Optional: if you want links on your homepage

const HomePage = () => {
  return (
    <div className="flex flex-col items-center justify-center text-center py-10 md:py-20">
      <header className="mb-12">
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold text-gray-800 mb-4 animate-fadeInDown">
          Hello, I'm [Your Name]!
        </h1>
        <p className="text-xl md:text-2xl text-gray-600 mb-8 animate-fadeInUp" style={{ animationDelay: '0.3s' }}>
          I'm a [Your Profession/Title] specializing in [Your Specialization].
        </p>
        <div className="space-y-4 sm:space-y-0 sm:space-x-6 animate-fadeInUp" style={{ animationDelay: '0.6s' }}>
          <Link
            to="/projects"
            className="inline-block bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-8 rounded-lg text-lg transition duration-300 ease-in-out shadow-md hover:shadow-lg transform hover:scale-105"
          >
            View My Work
          </Link>
          <Link
            to="/contact"
            className="inline-block bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-3 px-8 rounded-lg text-lg transition duration-300 ease-in-out shadow-md hover:shadow-lg transform hover:scale-105"
          >
            Get In Touch
          </Link>
        </div>
      </header>

      <section className="my-12 md:my-16 w-full max-w-3xl px-4 animate-fadeIn" style={{ animationDelay: '0.9s' }}>
        <h2 className="text-2xl sm:text-3xl font-semibold text-gray-700 mb-6">
          A Little About What I Do
        </h2>
        <p className="text-md sm:text-lg text-gray-600 leading-relaxed">
          Welcome to my personal space on the web! Here you'll find a collection of my projects, insights into my skills, and ways to connect with me. I'm passionate about creating [mention something specific, e.g., 'efficient and user-friendly web applications', 'innovative machine learning models', 'engaging digital experiences']. Explore my work and let's discuss how we can collaborate.
        </p>
      </section>

      {/* Basic CSS for animations - add to your src/index.css if you want them */}
      {/* @keyframes fadeInDown { from { opacity:0; transform:translateY(-20px); } to { opacity:1; transform:translateY(0); } }
      @keyframes fadeInUp { from { opacity:0; transform:translateY(20px); } to { opacity:1; transform:translateY(0); } }
      @keyframes fadeIn { from { opacity:0; } to { opacity:1; } }
      .animate-fadeInDown { animation:fadeInDown 0.6s ease-out forwards; }
      .animate-fadeInUp { animation:fadeInUp 0.6s ease-out forwards; }
      .animate-fadeIn { animation:fadeIn 0.8s ease-out forwards; }
      */}

    </div>
  );
};

export default HomePage; // Make sure to export the component
