// frontend/src/pages/admin/AdminEducationPage.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api'; // Your Axios instance
import { useAuth } from '../../contexts/AuthContext'; // To ensure admin access and send token

// We will create these Modals/Forms in a subsequent step or as separate components
// const AddEducationModal = ({ show, onClose, onSave }) => { /* ... */ };
// const EditEducationModal = ({ show, onClose, educationData, onSave }) => { /* ... */ };

const AdminEducationPage = () => {
  const [educationHistory, setEducationHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAdmin } = useAuth(); // For conditional rendering if needed, though route is protected

  // TODO: Add state for showing Add/Edit Modals
  // const [showAddModal, setShowAddModal] = useState(false);
  // const [editingEducation, setEditingEducation] = useState(null); // null or education object

  const fetchEducationHistory = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const { data } = await api.get('/education'); // Public GET endpoint
      setEducationHistory(data);
    } catch (err) {
      console.error("Error fetching education history:", err);
      setError(err.response?.data?.message || "Failed to load education history.");
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchEducationHistory();
  }, []);

  const handleAddEducation = () => {
    // TODO: setShowAddModal(true);
    alert("Add Education modal/form would open here.");
  };

  const handleEditEducation = (eduEntry) => {
    // TODO: setEditingEducation(eduEntry); // This would trigger an edit modal
    alert(`Edit functionality for "${eduEntry.degree} at ${eduEntry.institution}" needs modal/form.`);
  };

  const handleDeleteEducation = async (id, degree) => {
    if (window.confirm(`Are you sure you want to delete the entry: "${degree}"?`)) {
      try {
        setIsLoading(true); // Indicate an action is in progress
        await api.delete(`/education/${id}`); // This will send token via AuthContext's Axios config
        alert("Education entry deleted successfully!");
        fetchEducationHistory(); // Refresh the list
      } catch (err) {
        console.error("Error deleting education entry:", err);
        alert(err.response?.data?.message || "Failed to delete education entry.");
        setIsLoading(false);
      }
    }
  };

  if (isLoading && educationHistory.length === 0) {
    return <div className="p-6 text-center text-gray-500 dark:text-gray-400">Loading education history...</div>;
  }

  if (error) {
    return <div className="p-6 text-center text-red-500 dark:text-red-400 bg-red-100 dark:bg-red-900/20 rounded-md">{error}</div>;
  }

  return (
    <div className="p-4 sm:p-6 bg-white dark:bg-slate-800 shadow-lg rounded-xl">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl sm:text-3xl font-bold text-gray-800 dark:text-gray-100">Manage Education</h1>
        <button
          onClick={handleAddEducation}
          className="btn-primary py-2 px-4 text-sm" // Using helper class from index.css
        >
          + Add Education
        </button>
      </div>

      {isLoading && educationHistory.length > 0 && <p className="text-sm text-center text-gray-500 dark:text-gray-400 my-4">Refreshing list...</p>}

      {educationHistory.length === 0 && !isLoading ? (
        <p className="text-gray-600 dark:text-gray-400 text-center py-6">No education entries found. Add your first one!</p>
      ) : (
        <div className="space-y-4">
          {educationHistory.map((edu) => (
            <div key={edu._id} className="p-4 border border-gray-200 dark:border-slate-700 rounded-lg shadow-sm bg-gray-50 dark:bg-slate-700/30">
              <h3 className="text-lg font-semibold text-indigo-700 dark:text-indigo-400">{edu.degree}</h3>
              <p className="text-md text-gray-700 dark:text-gray-300">{edu.institution}</p>
              <p className="text-sm text-gray-500 dark:text-gray-400">
                {edu.startDate} - {edu.endDate} {edu.location && `| ${edu.location}`}
              </p>
              {edu.gpa && <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">GPA: {edu.gpa}</p>}
              {edu.description && <p className="mt-2 text-sm text-gray-600 dark:text-gray-300 whitespace-pre-wrap">{edu.description}</p>}
              <div className="mt-4 flex space-x-3 justify-end">
                <button 
                  onClick={() => handleEditEducation(edu)} 
                  className="btn-secondary py-1 px-3 text-xs" // Using helper class
                >
                  Edit
                </button>
                <button 
                  onClick={() => handleDeleteEducation(edu._id, edu.degree)} 
                  className="bg-red-500 hover:bg-red-600 text-white font-medium py-1 px-3 rounded-md text-xs shadow-sm dark:bg-red-600 dark:hover:bg-red-700 disabled:opacity-70"
                  disabled={isLoading}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
      {/* TODO: Add AddEducationModal and EditEducationModal components here, controlled by showAddModal and editingEducation state */}
    </div>
  );
};

export default AdminEducationPage;
