// frontend/src/pages/AboutPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { getSkills, Skill } from '../services/skillService'; // Added updateSkill
// Consider importing icons if you have them:
// import { FaPlus, FaEdit, FaTrash, FaTimes } from 'react-icons/fa';

// --- Static Data (Keep for other sections for now) ---
const aboutMeData = {
  introduction: ["Hello! I'm Harisharan, a passionate Full Stack Developer..."],
  philosophy: "I believe that the best solutions come from..."
};
const educationData = [/* Your static education data */];
const certificationsData = [/* Your static certifications data */];
const achievementsData = [/* Your static achievements data */];
// --- End Static Data ---


const AboutPage = () => {
  const { isAdmin } = useAuth();

  const [skills, setSkills] = useState([]);
  const [loadingSkills, setLoadingSkills] = useState(true);
  const [errorSkills, setErrorSkills] = useState(null);

  // --- State for Edit Skill Modal ---
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [currentEditingSkill, setCurrentEditingSkill] = useState(null); // Will hold { _id, name, category }
  const [editFormState, setEditFormState] = useState({ name: '', category: '' });
  const [editError, setEditError] = useState(null);
  // --- End Edit Skill Modal State ---

  useEffect(() => {
  const fetchSkillData = async () => {
    try {
      setLoadingSkills(true);
      const data = await getSkillCategories(); // Call the corrected service function
      setSkillCategories(data); // Assuming your state is named skillCategories
      setErrorSkills(null);
    } catch (error) {
      setErrorSkills(error.message || 'Failed to fetch skill categories');
      console.error("Error fetching skill categories:", error);
    } finally {
      setLoadingSkills(false);
    }
  };
  fetchSkillData();
}, []);
  const groupSkillsByCategory = (skillList) => {
    if (!skillList || skillList.length === 0) return {};
    return skillList.reduce((acc, skill) => {
      const category = skill.category || 'Other';
      if (!acc[category]) acc[category] = [];
      acc[category].push(skill);
      return acc;
    }, {});
  };
  const groupedSkills = groupSkillsByCategory(skills);

  // --- Admin Action Handlers ---
  const handleAddNewSkill = () => {
    console.log('Admin wants to add a new skill');
    // TODO: Implement Add Skill Modal/Form
  };

  const openEditModal = (skill) => {
    setCurrentEditingSkill(skill);
    setEditFormState({ name: skill.name, category: skill.category });
    setEditError(null); // Clear previous errors
    setIsEditModalOpen(true);
  };

  const closeEditModal = () => {
    setIsEditModalOpen(false);
    setCurrentEditingSkill(null);
    setEditFormState({ name: '', category: '' });
    setEditError(null);
  };

  const handleEditFormChange = (e) => {
    const { name, value } = e.target;
    setEditFormState(prevState => ({ ...prevState, [name]: value }));
  };

  const handleUpdateSkillSubmit = async (e) => {
    e.preventDefault();
    if (!currentEditingSkill) return;
    setEditError(null); // Clear previous errors

    try {
      const updatedSkill = await updateSkill(currentEditingSkill._id, editFormState);
      // Update the skills state locally
      setSkills(prevSkills => 
        prevSkills.map(s => s._id === updatedSkill._id ? updatedSkill : s)
      );
      closeEditModal();
    } catch (error) {
      console.error('Failed to update skill:', error);
      setEditError(error.message || 'Failed to update skill. Please try again.');
    }
  };

  const handleDeleteSkill = (skill) => {
    console.log('Admin wants to delete skill:', skill);
    // TODO: Implement Delete Skill Confirmation and API call
  };
  // --- End Admin Action Handlers ---

  return (
    <> {/* Using Fragment to allow modal to be outside main layout flow if needed */}
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-16 space-y-16 md:space-y-20">

        <div className="text-center">
          <h1 className="text-4xl sm:text-5xl font-bold text-gray-900 dark:text-white mb-4 transition-colors duration-300">
            About Me
          </h1>
          <p className="text-lg text-gray-600 dark:text-gray-400 max-w-3xl mx-auto transition-colors duration-300">
            A little about my journey, my passion for development, and what drives me.
          </p>
        </div>

        {/* Who I Am Section */}
        <section id="who-i-am" className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-xl transition-colors duration-300">
          {/* ... Who I Am JSX ... */}
            <h2 className="text-3xl font-semibold text-gray-800 dark:text-gray-100 mb-6">Who I Am</h2>
            <div className="space-y-4 text-gray-700 dark:text-gray-300 leading-relaxed text-base sm:text-lg">
              {aboutMeData.introduction.map((paragraph, index) => (
                <p key={index}>{paragraph}</p>
              ))}
              {aboutMeData.philosophy && (
                <p className="mt-6 italic border-l-4 border-indigo-500 dark:border-indigo-400 pl-4 py-3 bg-indigo-50 dark:bg-slate-700/60 rounded-r-md">
                  "{aboutMeData.philosophy}"
                </p>
              )}
            </div>
        </section>

        {/* Skills Section (with Admin Controls) */}
        <section id="skills">
          <div className="flex flex-col sm:flex-row justify-between items-center mb-8 sm:mb-10">
              <h2 className="text-3xl font-bold text-gray-900 dark:text-white text-center sm:text-left flex-grow mb-4 sm:mb-0">
                  Technical Skills
              </h2>
              {isAdmin && (
                <button
                  onClick={handleAddNewSkill}
                  className="bg-green-500 hover:bg-green-600 dark:bg-green-600 dark:hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-lg shadow-md transition duration-300 ease-in-out text-sm"
                >
                  Add New Skill
                </button>
              )}
          </div>

          {loadingSkills && ( <div className="text-center py-10 dark:text-gray-300"><p>Loading skills...</p></div> )}
          {errorSkills && ( <div className="text-center py-10 text-red-500 dark:text-red-400 bg-red-100 dark:bg-red-900/30 p-4 rounded-md"><p>Error: {errorSkills}</p></div> )}

          {!loadingSkills && !errorSkills && skills.length === 0 && (
              <div className="text-center py-10 text-gray-500 dark:text-gray-400 bg-gray-50 dark:bg-slate-800 p-6 rounded-xl shadow-md">
                  <p>No skills have been added yet.</p>
                  {isAdmin && !loadingSkills && (
                      <button
                          onClick={handleAddNewSkill}
                          className="mt-4 bg-green-500 hover:bg-green-600 dark:bg-green-600 dark:hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-lg shadow-md transition duration-300 ease-in-out text-sm"
                      >
                          Add First Skill
                      </button>
                  )}
              </div>
          )}

          {!loadingSkills && !errorSkills && skills.length > 0 && (
            <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-xl transition-colors duration-300">
              {Object.entries(groupedSkills).map(([category, skillsInCategory]) => (
                <div key={category} className="mb-8 last:mb-0">
                  <h3 className="text-xl sm:text-2xl font-semibold text-indigo-600 dark:text-indigo-400 mb-4 border-b-2 border-indigo-200 dark:border-slate-700 pb-2">
                    {category}
                  </h3>
                  <div className="flex flex-wrap gap-3">
                    {skillsInCategory.map((skill) => (
                      <div 
                          key={skill._id} 
                          className="group bg-indigo-100 dark:bg-slate-700 text-indigo-800 dark:text-indigo-300 px-4 py-2 rounded-full text-sm font-medium shadow-sm flex items-center gap-2"
                      >
                        <span>{skill.name}</span>
                        {isAdmin && (
                          <div className="flex gap-1 items-center opacity-0 group-hover:opacity-100 transition-opacity duration-200">
                            <button 
                              onClick={() => openEditModal(skill)} 
                              title="Edit Skill"
                              className="text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300 p-1 text-xs" // Smaller text/padding for icons
                            >
                              {/* <FaEdit /> Using react-icons */} E
                            </button>
                            <button 
                              onClick={() => handleDeleteSkill(skill)} 
                              title="Delete Skill"
                              className="text-red-600 hover:text-red-800 dark:text-red-400 dark:hover:text-red-300 p-1 text-xs"
                            >
                              {/* <FaTrash /> Using react-icons */} X
                            </button>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>

        {/* Education, Certifications, Achievements Sections (Still static for brevity) */}
        {/* ... Your existing JSX for Education ... */}
        {/* ... Your existing JSX for Certifications ... */}
        {/* ... Your existing JSX for Achievements ... */}

      </div>

      {/* --- Edit Skill Modal --- */}
      {isEditModalOpen && currentEditingSkill && (
        <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center p-4 z-50 transition-opacity duration-300 ease-in-out">
          <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-300 ease-in-out scale-100">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-semibold text-gray-800 dark:text-white">Edit Skill</h2>
              <button 
                onClick={closeEditModal} 
                className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 text-2xl"
                aria-label="Close modal"
              >
                {/* <FaTimes /> Using react-icons */} &times;
              </button>
            </div>

            <form onSubmit={handleUpdateSkillSubmit} className="space-y-6">
              <div>
                <label htmlFor="skillName" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  Skill Name
                </label>
                <input
                  type="text"
                  name="name"
                  id="skillName"
                  value={editFormState.name}
                  onChange={handleEditFormChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 dark:border-slate-600 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-slate-700 dark:text-white sm:text-sm"
                />
              </div>
              <div>
                <label htmlFor="skillCategory" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                  Category
                </label>
                <input
                  type="text"
                  name="category"
                  id="skillCategory"
                  value={editFormState.category}
                  onChange={handleEditFormChange}
                  required
                  className="w-full px-4 py-2 border border-gray-300 dark:border-slate-600 rounded-lg shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-slate-700 dark:text-white sm:text-sm"
                />
                {/* You could also make this a select dropdown if you have predefined categories */}
              </div>

              {editError && (
                <p className="text-sm text-red-600 dark:text-red-400 bg-red-100 dark:bg-red-900/30 p-3 rounded-md">{editError}</p>
              )}

              <div className="flex justify-end gap-4 pt-2">
                <button
                  type="button"
                  onClick={closeEditModal}
                  className="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-slate-700 hover:bg-gray-200 dark:hover:bg-slate-600 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 dark:hover:bg-indigo-500 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  Save Changes
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
      {/* --- End Edit Skill Modal --- */}
    </>
  );
};

export default AboutPage;
