// frontend/src/App.js
import React from 'react';
import { useAuth } from './contexts/AuthContext'; // For testing what App receives

function App() {
  const { currentUser, isAdmin, isLoadingAuth } = useAuth();

  // Log what App receives from AuthContext
  console.log('[App.js Simplified] Rendering. isLoadingAuth:', isLoadingAuth, 'isAdmin:', isAdmin, 'User:', currentUser ? currentUser.name : 'No User');

  // If AuthContext is still loading, show a message from App.js
  if (isLoadingAuth) {
    return (
      <div style={{ padding: '20px', border: '2px dashed red', textAlign: 'center', fontSize: '1.5em' }}>
        App Component is waiting for authentication...
      </div>
    );
  }

  // If AuthContext has loaded, show this basic UI
  return (
    <div style={{ padding: '20px', border: '2px solid green', minHeight: '100vh' }}>
      <h1>Hello from the SIMPLIFIED App.js!</h1>
      <p>If you see this, the App component itself is rendering after AuthProvider.</p>
      {currentUser ? (
        <div style={{ marginTop: '20px', border: '1px solid blue', padding: '10px' }}>
          <p><strong>User Logged In:</strong> {currentUser.name} ({currentUser.email})</p>
          <p><strong>Admin Status:</strong> {isAdmin ? 'Yes, this is an Admin.' : 'No, not an Admin.'}</p>
        </div>
      ) : (
        <p style={{ marginTop: '20px', color: 'red' }}>
          No user is currently logged in according to AuthContext.
        </p>
      )}
      <p style={{ marginTop: '20px' }}>
        (All original Navbar, Footer, and Routes are temporarily removed for this test.)
      </p>
    </div>
  );
}

export default App;
