// portfolio_py/frontend/src/pages/admin/AboutPage/AboutPage.js
import React from 'react';
// Import components from the SAME directory
import WhoIAmSection from './WhoIAmSection';
import SkillsSection from './SkillsSection';
import EducationSection from './EducationSection';
import CertificationsSection from './CertificationsSection';
import AchievementsSection from './AchievementsSection';

// Static data can be defined here or passed from a shared constants file if preferred
const aboutMeData = {
  introduction: [
    "Hello! I'm Harisharan, a passionate Full Stack Developer with a strong foundation in creating dynamic and responsive web applications.",
    "My journey in tech is driven by a constant desire to learn, innovate, and solve complex problems with elegant and efficient solutions."
  ],
  philosophy: "I believe that the best solutions come from a deep understanding of user needs, collaborative teamwork, and a commitment to clean, maintainable code."
};

const certificationsDataStatic = [
  { name: "Full Stack Web Development (MERN)", issuer: "Webstack Academy", date: "Sep 2024" }, // Example data
  // Add more static certifications if needed
];
const achievementsDataStatic = [
  "Solved over 300+ problems in various coding platforms.",
  "Executive Member, Technical Club: Organized and hosted multiple tech events, fostering innovation and collaboration among students.",
  "Managed event planning and technical workshops to enhance learning opportunities."
  // Add more static achievements if needed
];


const AboutPage = () => {
  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-16 space-y-16 md:space-y-20">
      <WhoIAmSection aboutMeData={aboutMeData} />
      <SkillsSection />
      <EducationSection />
      <CertificationsSection />
      <AchievementsSection achievementsData={achievementsDataStatic} />
    </div>
  );
};

export default AboutPage;
