// portfolio_py/frontend/src/pages/ContactPage.js
import React, { useState, useEffect } from 'react';
import ContactForm from '../components/contact/ContactForm';
import { useAuth } from '../contexts/AuthContext';
import * as socialLinkService from '../services/socialLinkService';
import { PlusCircle, Edit3, Trash2, Smartphone, Github, Linkedin, Twitter as TwitterIconLucide, Code, ExternalLink, Globe, Link as LinkIcon, Mail, Info } from 'lucide-react';
import { motion } from 'framer-motion';

const iconComponents = {
  github: Github,
  linkedin: Linkedin,
  twitter: TwitterIconLucide,
  leetcode: Code,
  whatsapp: Smartphone,
  website: Globe,
  email: Mail,
  externallink: ExternalLink,
  info: Info,
  link: LinkIcon,
  default: LinkIcon,
};

const SocialLinkFormModal = ({ show, onClose, onSave, currentLink, isLoading }) => {
    const isEditing = !!currentLink?._id;
    const [formData, setFormData] = useState({ name: '', url: '', iconName: 'default', isEnabled: true });

    useEffect(() => {
        if (show) {
            setFormData(isEditing && currentLink ?
                { name: currentLink.name || '', url: currentLink.url || '', iconName: currentLink.iconName || 'default', isEnabled: currentLink.isEnabled } :
                { name: '', url: '', iconName: 'default', isEnabled: true }
            );
        }
    }, [currentLink, isEditing, show]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.name.trim() || !formData.url.trim()) { alert('Platform Name and URL are required.'); return; }
        onSave(formData, currentLink?._id);
    };

    if (!show) return null;

    return (
        <div className="fixed inset-0 bg-black/60 z-50 flex justify-center items-center p-4">
            <div className="bg-white dark:bg-slate-800 p-6 rounded-lg w-full max-w-md">
                <h2 className="text-xl font-semibold mb-4">{isEditing ? 'Edit Link' : 'Add New Link'}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    {/* Form fields... */}
                    <div className="flex justify-end gap-3">
                        <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
                        <button type="submit" disabled={isLoading} className="btn-primary">Save</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

const ContactPage = () => {
    const { isAdmin } = useAuth();
    const [socialLinks, setSocialLinks] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingLink, setEditingLink] = useState(null);

    const fetchLinks = async () => {
        setIsLoading(true);
        try {
            const data = isAdmin ? await socialLinkService.getAdminSocialLinks() : await socialLinkService.getPublicSocialLinks();
            setSocialLinks(data || []);
        } catch (error) {
            console.error("Failed to fetch social links:", error);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchLinks();
    }, [isAdmin]);

    const handleSave = async (formData, id) => {
        try {
            if (id) {
                await socialLinkService.updateSocialLink(id, formData);
            } else {
                await socialLinkService.addSocialLink(formData);
            }
            setIsModalOpen(false);
            fetchLinks();
        } catch (err) {
            alert(`Error: ${err.response?.data?.message || err.message}`);
        }
    };

    const handleDelete = async (id, name) => {
        if (window.confirm(`Delete "${name}"?`)) {
            try {
                await socialLinkService.deleteSocialLink(id);
                fetchLinks();
            } catch (err) {
                alert(`Error: ${err.response?.data?.message || err.message}`);
            }
        }
    };

    const openEditModal = (link) => { setEditingLink(link); setIsModalOpen(true); };
    const openNewModal = () => { setEditingLink(null); setIsModalOpen(true); };

    return (
        <>
            <SocialLinkFormModal show={isModalOpen} onClose={() => setIsModalOpen(false)} onSave={handleSave} currentLink={editingLink} />
            <motion.div
              className="bg-slate-50 dark:bg-slate-900 py-12 px-4"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.8 }}
            >
                <div className="container mx-auto max-w-2xl">
                    <div className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-2xl">
                        <div className="mb-10"><ContactForm /></div>
                        <div className="pt-8 border-t border-gray-200 dark:border-slate-700">
                            <div className="flex justify-center items-center gap-4 mb-6">
                                <h2 className="text-xl font-semibold">Connect With Me</h2>
                                {isAdmin && (
                                    <button onClick={openNewModal} className="btn-primary py-1 px-3 text-sm flex items-center">
                                        <PlusCircle size={16} className="mr-1.5" /> Add
                                    </button>
                                )}
                            </div>
                            {isLoading ? <p className="text-center">Loading...</p> : (
                                <div className="flex flex-wrap justify-center items-center gap-4">
                                    {socialLinks.map((link) => {
                                        const IconComponent = iconComponents[link.iconName.toLowerCase()] || iconComponents.default;
                                        return (
                                            <div key={link._id} className="relative group">
                                                <a href={link.url} target="_blank" rel="noopener noreferrer" className={`btn-secondary flex items-center gap-2 py-2.5 px-5 ${!link.isEnabled && 'opacity-40'}`}>
                                                    <IconComponent size={20} />
                                                    <span>{link.name}</span>
                                                </a>
                                                {isAdmin && (
                                                    <div className="absolute -top-2 -right-2 flex gap-1">
                                                        <button onClick={() => openEditModal(link)} className="p-1.5 bg-yellow-400 text-black rounded-full shadow-lg"><Edit3 size={12} /></button>
                                                        <button onClick={() => handleDelete(link._id, link.name)} className="p-1.5 bg-red-600 text-white rounded-full shadow-lg"><Trash2 size={12} /></button>
                                                    </div>
                                                )}
                                            </div>
                                        );
                                    })}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </motion.div>
        </>
    );
};

export default ContactPage;
