// frontend/src/pages/AboutPage.js
import React from 'react';
// import { useAuth } from '../contexts/AuthContext';

// --- Placeholder Data (Ensure this is YOUR information) ---
const aboutMeData = {
  introduction: [
    "Hello! I'm Harisharan, a passionate Full Stack Developer with a keen interest in building efficient, scalable, and user-friendly web applications. My journey in tech has been driven by a continuous desire to learn and apply new technologies to solve real-world problems.",
    "I specialize in the MERN stack (MongoDB, Express.js, React, Node.js) and have a strong foundation in JavaScript, along with experience in building responsive and dynamic user interfaces with Tailwind CSS.",
    "I thrive on turning complex ideas into tangible digital experiences. This portfolio showcases some of my work and the skills I've developed. I'm always eager to learn, adapt, and collaborate on exciting projects."
  ],
  philosophy: "I believe that the best solutions come from a blend of technical expertise, a deep understanding of user needs, and a commitment to clean, maintainable code."
};

const educationData = [
  {
    id: "edu1",
    degree: "Master's in Computer Science (Example)",
    institution: "Example University",
    location: "City, Country",
    startDate: "Jan 202X",
    endDate: "Dec 202X",
    gpa: "3.8/4.0 (Example)",
    description: "Key courses: Advanced Web Development, Database Systems, AI."
  },
  {
    id: "edu2",
    degree: "Bachelor of Technology in Computer Science (Example)",
    institution: "Example College",
    location: "City, Country",
    startDate: "Aug 201X",
    endDate: "May 202X",
    gpa: "8.5/10 (Example)",
    description: "Relevant coursework: Data Structures, Algorithms, Web Design."
  },
];

const certificationsData = [
  {
    id: "cert1",
    name: "MERN Stack Full Development Certification (Example)",
    issuingOrganization: "Tech Cert Inc.",
    dateIssued: "Jun 202X",
    credentialUrl: "#", 
  },
  {
    id: "cert2",
    name: "Advanced Tailwind CSS (Example)",
    issuingOrganization: "Design Masters Co.",
    dateIssued: "Mar 202X",
  },
];

const skillsData = {
  "Languages": ["JavaScript (ES6+)", "Python", "Java", "C++", "HTML5", "CSS3", "SQL"],
  "Frameworks & Libraries": ["React", "Node.js", "Express.js", "Tailwind CSS", "Mongoose"],
  "Databases": ["MongoDB"],
  "Tools & Platforms": ["Git & GitHub", "VS Code", "Postman", "Docker (Basic)"],
  "Concepts": ["RESTful APIs", "Agile Methodologies", "Problem Solving", "Data Structures"]
};

const achievementsData = [
  "Successfully developed and deployed 3+ full-stack web applications.",
  "Led a team in a university project, delivering ahead of schedule.",
  "Achieved 95% positive feedback on a recent client project.",
];
// --- End Placeholder Data ---


const AboutPage = () => {
  // const { isAdmin } = useAuth(); 

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-16 space-y-16 md:space-y-20"> {/* Added more vertical spacing */}

      <div className="text-center">
        <h1 className="text-4xl sm:text-5xl font-bold text-gray-900 dark:text-white mb-4 transition-colors duration-300">
          About Me
        </h1>
        <p className="text-lg text-gray-600 dark:text-gray-400 max-w-3xl mx-auto transition-colors duration-300">
          A little about my journey, my passion for development, and what drives me.
        </p>
      </div>

      {/* Who I Am Section */}
      <section id="who-i-am" className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-xl transition-colors duration-300">
        <h2 className="text-3xl font-semibold text-gray-800 dark:text-gray-100 mb-6">Who I Am</h2>
        <div className="space-y-4 text-gray-700 dark:text-gray-300 leading-relaxed text-base sm:text-lg">
          {aboutMeData.introduction.map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
          {aboutMeData.philosophy && (
            <p className="mt-6 italic border-l-4 border-indigo-500 dark:border-indigo-400 pl-4 py-3 bg-indigo-50 dark:bg-slate-700/60 rounded-r-md">
              "{aboutMeData.philosophy}"
            </p>
          )}
        </div>
      </section>

      {/* Skills Section */}
      <section id="skills">
        <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-10 text-center">Technical Skills</h2>
        <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-xl transition-colors duration-300">
          {Object.entries(skillsData).map(([category, skillsList]) => (
            <div key={category} className="mb-8 last:mb-0">
              <h3 className="text-xl sm:text-2xl font-semibold text-indigo-600 dark:text-indigo-400 mb-4 border-b-2 border-indigo-200 dark:border-slate-700 pb-2">
                {category}
              </h3>
              <div className="flex flex-wrap gap-3">
                {skillsList.map((skill) => (
                  <span 
                    key={skill} 
                    className="bg-indigo-100 dark:bg-slate-700 text-indigo-800 dark:text-indigo-300 px-4 py-2 rounded-full text-sm font-medium shadow-sm"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Education Section */}
      <section id="education">
        <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-10 text-center">Education</h2>
        <div className="space-y-8">
          {educationData.map((edu) => (
            <div 
              key={edu.id} 
              className="bg-white dark:bg-slate-800 p-6 rounded-xl shadow-xl transition-all duration-300 hover:shadow-2xl border border-gray-200 dark:border-slate-700 hover:border-indigo-300 dark:hover:border-indigo-500"
            >
              <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-2">
                <h3 className="text-xl font-semibold text-gray-800 dark:text-gray-100">{edu.degree}</h3>
                <p className="text-sm text-indigo-600 dark:text-indigo-400 mt-1 sm:mt-0 whitespace-nowrap">{edu.startDate} - {edu.endDate}</p>
              </div>
              <p className="text-md font-medium text-gray-700 dark:text-gray-300">{edu.institution}</p>
              <p className="text-sm text-gray-500 dark:text-gray-400 mb-3">{edu.location}</p>
              {edu.gpa && <span className="inline-block bg-gray-200 dark:bg-slate-700 text-gray-700 dark:text-gray-300 px-3 py-1 rounded-full text-xs font-semibold mb-3">GPA: {edu.gpa}</span>}
              {edu.description && <p className="mt-2 text-sm text-gray-600 dark:text-gray-400 leading-relaxed">{edu.description}</p>}
            </div>
          ))}
        </div>
      </section>

      {/* Certifications Section */}
      <section id="certifications">
        <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-10 text-center">Licenses & Certifications</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 lg:gap-8">
          {certificationsData.length > 0 ? certificationsData.map((cert) => (
            <div 
              key={cert.id} 
              className="bg-white dark:bg-slate-800 p-6 rounded-xl shadow-xl flex flex-col items-start transition-all duration-300 hover:shadow-2xl border border-gray-200 dark:border-slate-700 hover:border-green-300 dark:hover:border-green-500"
            >
              {/* Optional: Certification Logo placeholder */}
              {/* {cert.logoUrl && <img src={cert.logoUrl} alt={`${cert.issuingOrganization} logo`} className="h-10 mb-4 self-start"/>} */}
              <h3 className="text-lg font-semibold text-gray-800 dark:text-gray-100">{cert.name}</h3>
              <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">{cert.issuingOrganization}</p>
              <p className="text-xs text-gray-500 dark:text-gray-500 mt-1 mb-3">Issued: {cert.dateIssued}</p>
              {cert.credentialUrl && cert.credentialUrl !== "#" ? ( // Check if URL is not just "#"
                <a 
                  href={cert.credentialUrl} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="mt-auto inline-block text-sm text-green-600 dark:text-green-400 hover:text-green-700 dark:hover:text-green-300 font-medium transition-colors"
                >
                  View Credential &rarr;
                </a>
              ) : (
                <span className="mt-auto text-sm text-gray-400 dark:text-gray-600">(No online credential)</span>
              )}
            </div>
          )) : (
            <p className="md:col-span-2 text-center text-gray-500 dark:text-gray-400">No certifications listed yet.</p>
          )}
