// frontend/src/pages/HomePage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // To check admin status
import api from '../services/api'; // Your Axios instance

// --- Placeholder for Edit Modals (we will define these properly later or as separate components) ---
const EditTextModal = ({ show, onClose, currentSettings, onSave, isLoading }) => {
  if (!show) return null;

  const [formData, setFormData] = useState({
    ownerName: currentSettings.ownerName || '',
    jobTitle: currentSettings.jobTitle || '',
    specialization: currentSettings.specialization || '',
    homePageIntroParagraph: currentSettings.homePageIntroParagraph || '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-75 z-50 flex justify-center items-center p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-lg animate-modalFadeIn">
        <style jsx global>{` @keyframes modalFadeIn { to { opacity: 1; transform: scale(1); } } .animate-modalFadeIn { animation: modalFadeIn 0.2s ease-out forwards; opacity:0; transform: scale(0.95); } `}</style>
        <h2 className="text-2xl font-semibold mb-4">Edit Homepage Content</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="ownerName" className="block text-sm font-medium text-gray-700">Your Name</label>
            <input type="text" name="ownerName" id="ownerName" value={formData.ownerName} onChange={handleChange} className="mt-1 input-style w-full" />
          </div>
          <div>
            <label htmlFor="jobTitle" className="block text-sm font-medium text-gray-700">Your Profession/Title</label>
            <input type="text" name="jobTitle" id="jobTitle" value={formData.jobTitle} onChange={handleChange} className="mt-1 input-style w-full" />
          </div>
          <div>
            <label htmlFor="specialization" className="block text-sm font-medium text-gray-700">Specialization/Tagline</label>
            <input type="text" name="specialization" id="specialization" value={formData.specialization} onChange={handleChange} className="mt-1 input-style w-full" />
          </div>
          <div>
            <label htmlFor="homePageIntroParagraph" className="block text-sm font-medium text-gray-700">Introductory Paragraph</label>
            <textarea name="homePageIntroParagraph" id="homePageIntroParagraph" rows="4" value={formData.homePageIntroParagraph} onChange={handleChange} className="mt-1 input-style w-full"></textarea>
          </div>
          <div className="flex justify-end space-x-3 pt-2">
            <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
            <button type="submit" disabled={isLoading} className="btn-primary disabled:opacity-70">
              {isLoading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const EditPhotoModal = ({ show, onClose, currentPhotoUrl, onSave, isLoading }) => {
  if (!show) return null;

  const [selectedPhotoFile, setSelectedPhotoFile] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedPhotoFile) {
      alert("Please select a new photo to upload.");
      return;
    }
    onSave(selectedPhotoFile);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-75 z-50 flex justify-center items-center p-4">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md animate-modalFadeIn">
       <style jsx global>{` @keyframes modalFadeIn { to { opacity: 1; transform: scale(1); } } .animate-modalFadeIn { animation: modalFadeIn 0.2s ease-out forwards; opacity:0; transform: scale(0.95); } `}</style>
        <h2 className="text-2xl font-semibold mb-4">Update Profile Photo</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {currentPhotoUrl && <img src={currentPhotoUrl} alt="Current profile" className="mx-auto h-32 w-32 rounded-full object-cover mb-4" />}
          <div>
            <label htmlFor="profilePhoto" className="block text-sm font-medium text-gray-700">New Photo (JPG, PNG, GIF, WebP)</label>
            <input type="file" name="profilePhoto" id="profilePhoto" accept="image/*" onChange={(e) => setSelectedPhotoFile(e.target.files[0])} required className="mt-1 file-input-style w-full" />
          </div>
          {selectedPhotoFile && <p className="text-xs text-gray-500">Selected: {selectedPhotoFile.name}</p>}
          <div className="flex justify-end space-x-3 pt-2">
            <button type="button" onClick={onClose} className="btn-secondary">Cancel</button>
            <button type="submit" disabled={isLoading} className="btn-primary disabled:opacity-70">
              {isLoading ? 'Uploading...' : 'Upload Photo'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
// --- End Placeholder Modals ---


const HomePage = () => {
  const { isAdmin, isLoadingAuth } = useAuth();
  const [siteSettings, setSiteSettings] = useState(null);
  const [isLoadingPageData, setIsLoadingPageData] = useState(true);
  const [errorPageData, setErrorPageData] = useState('');
  const [actionMessage, setActionMessage] = useState(''); // For success/error of updates

  // State for controlling modals
  const [showEditIntroModal, setShowEditIntroModal] = useState(false);
  const [showEditPhotoModal, setShowEditPhotoModal] = useState(false);

  const defaultProfileImageUrl = "/images/default-profile.png"; // Default if nothing is set

  const fetchSiteSettings = async () => {
    setIsLoadingPageData(true);
    try {
      const { data } = await api.get('/settings');
      setSiteSettings(data);
    } catch (err) {
      console.error("Error fetching site settings:", err);
      setErrorPageData(err.response?.data?.message || "Could not load site settings.");
      // Initialize with default structure if fetch fails, so page doesn't break
      setSiteSettings({ 
        ownerName: '[Your Name]', 
        jobTitle: '[Your Title]', 
        specialization: '[Your Specialization]', 
        homePageIntroParagraph: 'Failed to load introduction. Please try again later.',
        profilePhotoUrl: defaultProfileImageUrl
      });
    } finally {
      setIsLoadingPageData(false);
    }
  };

  useEffect(() => {
    // Fetch settings only when auth status is resolved (to avoid fetching if user isn't determined yet, though GET /settings is public)
    if (!isLoadingAuth) {
      fetchSiteSettings();
    }
  }, [isLoadingAuth]); // Re-fetch if auth status changes (might not be necessary here but good practice)


  const handleUpdateTextSettings = async (formData) => {
    setIsLoadingPageData(true); // Use the page data loading indicator
    setActionMessage('');
    try {
      const { data } = await api.put('/settings', formData);
      setSiteSettings(data); // Update local state with new settings from response
      setShowEditIntroModal(false);
      setActionMessage({text: 'Homepage content updated successfully!', type: 'success'});
    } catch (err) {
      console.error("Error updating text settings:", err);
      setActionMessage({text: err.response?.data?.message || 'Failed to update content.', type: 'error'});
    } finally {
      setIsLoadingPageData(false);
      setTimeout(() => setActionMessage(''), 3000);
    }
  };

  const handleProfilePhotoUpload = async (photoFile) => {
    setIsLoadingPageData(true);
    setActionMessage('');
    const formData = new FormData();
    formData.append('profilePhoto', photoFile);

    try {
      const { data } = await api.post('/settings/profile-photo', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setSiteSettings(data.settings); // Assuming backend returns all settings including the new photo URL
      setShowEditPhotoModal(false);
      setActionMessage({text: 'Profile photo updated successfully!', type: 'success'});
    } catch (err) {
      console.error("Error uploading profile photo:", err);
      setActionMessage({text: err.response?.data?.message || 'Failed to upload photo.', type: 'error'});
    } finally {
      setIsLoadingPageData(false);
      setTimeout(() => setActionMessage(''), 3000);
    }
  };


  if (isLoadingAuth || (isLoadingPageData && !siteSettings)) {
    return <div className="text-center py-20">Loading Page Content...</div>;
  }

  // Display error if settings couldn't be loaded and not overridden by admin actions
  if (errorPageData && !siteSettings.ownerName.includes('[')) { // A bit hacky check to see if defaults are still there
    return <div className="text-center py-20 text-red-500">{errorPageData}</div>;
  }

  // Use default values from schema if settings are somehow null after loading
  const settings = siteSettings || { 
    ownerName: '[Your Name]', jobTitle: '[Your Title]', specialization: '[Your Specialization]', 
    homePageIntroParagraph: 'Default intro paragraph.', profilePhotoUrl: defaultProfileImageUrl 
  };


  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-20">
      {actionMessage && (
        <div className={`p-4 mb-6 rounded-md text-sm text-center ${actionMessage.type === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`} role="alert">
          {actionMessage.text}
        </div>
      )}
      <div className="flex flex-col md:flex-row items-center justify-center md:justify-between gap-8 md:gap-12">
        <div className="md:w-3/5 lg:w-1/2 text-center md:text-left">
          {/* Text Content Section */}
          <div className="relative group">
            <h1 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold text-gray-800 leading-tight mb-4">
              Hello, I'm <span className="text-indigo-600">{settings.ownerName || '[Your Name]'}!</span>
            </h1>
            <p className="text-lg sm:text-xl lg:text-2xl text-gray-600 mb-8 leading-relaxed">
              A passionate {settings.jobTitle || '[Your Profession/Title]'} specializing in {settings.specialization || '[Your Specialization]'}.
            </p>
            {isAdmin && (
              <button 
                onClick={() => setShowEditIntroModal(true)}
                className="absolute top-0 right-0 mt-1 mr-1 p-1.5 bg-gray-200 hover:bg-gray-300 rounded-full text-gray-600 opacity-0 group-hover:opacity-100 transition-opacity"
                title="Edit Text Content"
              >
                ✏️ {/* Placeholder for an edit icon */}
              </button>
            )}
          </div>
          <div className="flex flex-col sm:flex-row justify-center md:justify-start gap-4">
            <Link to="/projects" className="inline-block bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-8 rounded-lg text-md sm:text-lg shadow-lg hover:shadow-xl transition-all duration-300 ease-in-out transform hover:-translate-y-0.5">
              View My Work
            </Link>
            <Link to="/contact" className="inline-block bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-3 px-8 rounded-lg text-md sm:text-lg shadow-lg hover:shadow-xl transition-all duration-300 ease-in-out transform hover:-translate-y-0.5">
              Get In Touch
            </Link>
          </div>
        </div>

        {/* Image Section */}
        <div className="md:w-2/5 lg:w-1/2 flex justify-center md:justify-end mt-8 md:mt-0">
          <div className="relative group w-64 h-64 sm:w-80 sm:h-80 lg:w-96 lg:h-96 rounded-full overflow-hidden shadow-2xl border-4 border-white transform transition-all duration-500 hover:scale-105">
            <img 
              src={settings.profilePhotoUrl || defaultProfileImageUrl} 
              alt={settings.ownerName || 'Profile photo'} 
              className="w-full h-full object-cover" 
              onError={(e) => { e.target.onerror = null; e.target.src = defaultProfileImageUrl; }}
            />
            {isAdmin && (
              <button 
                onClick={() => setShowEditPhotoModal(true)}
                className="absolute top-2 right-2 p-2 bg-black bg-opacity-40 hover:bg-opacity-60 rounded-full text-white opacity-0 group-hover:opacity-100 transition-opacity"
                title="Change Profile Photo"
              >
                ✏️ {/* Placeholder for an edit icon */}
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Introductory Paragraph Section */}
      <section className="my-12 md:my-20 w-full max-w-3xl mx-auto px-4 text-center md:text-left">
         <div className="relative group">
            <h2 className="text-2xl sm:text-3xl font-semibold text-gray-700 mb-6">
              A Little About Me
            </h2>
            <p className="text-md sm:text-lg text-gray-600 leading-relaxed">
              {settings.homePageIntroParagraph || 'Default intro paragraph. Update this from the admin panel.'}
            </p>
            {isAdmin && (
                <button 
                  onClick={() => setShowEditIntroModal(true)} // This modal will edit all text fields
                  className="absolute top-0 right-0 -mt-2 mr-0 p-1.5 bg-gray-200 hover:bg-gray-300 rounded-full text-gray-600 opacity-0 group-hover:opacity-100 transition-opacity"
                  title="Edit Introduction"
                >
                  ✏️
                </button>
            )}
          </div>
      </section>

      {/* Modals for Editing */}
      {isAdmin && (
        <>
          <EditTextModal 
            show={showEditIntroModal} 
            onClose={() => setShowEditIntroModal(false)} 
            currentSettings={siteSettings || {}} // Pass current settings to prefill form
            onSave={handleUpdateTextSettings}
            isLoading={isLoadingData} // Pass loading state for the save button
          />
          <EditPhotoModal 
            show={showEditPhotoModal} 
            onClose={() => setShowEditPhotoModal(false)}
            currentPhotoUrl={siteSettings?.profilePhotoUrl || defaultProfileImageUrl}
            onSave={handleProfilePhotoUpload}
            isLoading={isLoadingData} // Pass loading state for the upload button
          />
        </>
      )}

      {/* Re-add input-style and file-input-style, btn-primary, btn-secondary to your src/index.css @layer components if you haven't */}
    </div>
  );
};

export default HomePage;
