// frontend/src/pages/AboutPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
// Corrected import: getSkillCategories and ensure updateSkill is imported if used
import { getSkillCategories, updateSkill } from '../services/skillService';

// --- Static Data (Keep for other sections for now) ---
const aboutMeData = {
  introduction: ["Hello! I'm Harisharan, a passionate Full Stack Developer..."],
  philosophy: "I believe that the best solutions come from..."
};
const educationData = [/* Your static education data */];
const certificationsData = [/* Your static certifications data */];
const achievementsData = [/* Your static achievements data */];
// --- End Static Data ---

const AboutPage = () => {
  const { isAdmin } = useAuth();

  // Renamed state to reflect it holds categories and their skills
  const [skillCategoriesData, setSkillCategoriesData] = useState([]);
  const [loadingSkills, setLoadingSkills] = useState(true);
  const [errorSkills, setErrorSkills] = useState(null);

  // --- State for Edit Skill Modal (This will need a rethink for category-based skills) ---
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [currentEditingSkill, setCurrentEditingSkill] = useState(null); // Holds the skill object being edited
  const [currentEditingCategoryId, setCurrentEditingCategoryId] = useState(null); // To know which category the skill belongs to
  const [editFormState, setEditFormState] = useState({ name: '', category: '' }); // 'category' field here might be misleading now
  const [editError, setEditError] = useState(null);
  // --- End Edit Skill Modal State ---

  useEffect(() => {
    const fetchSkillData = async () => {
      try {
        setLoadingSkills(true);
        const data = await getSkillCategories(); // Using the correctly imported and defined function
        setSkillCategoriesData(data);         // Using the correctly named state setter
        setErrorSkills(null);
      } catch (error) {
        setErrorSkills(error.message || 'Failed to fetch skill categories');
        console.error("Error fetching skill categories:", error);
      } finally {
        setLoadingSkills(false);
      }
    };
    fetchSkillData();
  }, []);

  // The groupSkillsByCategory function is likely NOT needed if your backend
  // at /api/skillcategories already returns data grouped by category like:
  // [ { name: "Languages", skills: [{name: "JS"}, {name: "Python"}] }, ... ]
  // If it returns a flat list even from /api/skillcategories, then this function would be needed.
  // For now, assuming data is already structured.

  // --- Admin Action Handlers (Placeholders - need to be adapted for categories/skills within them) ---
  const handleAddCategory = () => console.log('TODO: Add New Category');
  const handleEditCategory = (category) => console.log('TODO: Edit Category:', category);
  const handleDeleteCategory = (category) => console.log('TODO: Delete Category:', category);
  const handleAddSkillToCategory = (category) => console.log('TODO: Add skill to category:', category.name);

  const openEditModal = (skill, categoryId) => {
    // This modal was designed for a flat skill structure.
    // For category-based skills, you'll need to decide what "editing a skill" means.
    // Does it mean changing its name? Moving to another category?
    // The current editFormState with 'name' and 'category' string is probably not right.
    console.warn("Opening edit modal for skill. Current modal is for flat structure.", skill);
    setCurrentEditingSkill(skill);
    setCurrentEditingCategoryId(categoryId); // Store category context
    setEditFormState({ name: skill.name, category: '' }); // 'category' here is placeholder
    setEditError(null);
    setIsEditModalOpen(true);
  };

  const closeEditModal = () => {
    setIsEditModalOpen(false);
    setCurrentEditingSkill(null);
    setCurrentEditingCategoryId(null);
    setEditFormState({ name: '', category: '' });
    setEditError(null);
  };

  const handleEditFormChange = (e) => {
    const { name, value } = e.target;
    setEditFormState(prevState => ({ ...prevState, [name]: value }));
  };

  const handleUpdateSkillSubmit = async (e) => {
    e.preventDefault();
    if (!currentEditingSkill) return;
    setEditError(null);

    // IMPORTANT: The 'updateSkill' service function currently calls PUT /api/skills/:id
    // This is likely NOT the correct endpoint if your skills are nested under categories.
    // You'll need a backend endpoint like PUT /api/skillcategories/:catId/skills/:skillId
    // and a corresponding service function.
    // For now, this will probably fail or update the wrong data structure.
    try {
      // This will call the old updateSkill, which might not be what you want.
      const updatedData = await updateSkill(currentEditingSkill._id, { name: editFormState.name }); // Only sending name for now
      console.log('Update response (from old /api/skills route):', updatedData);
      // The logic to update skillCategoriesData will be complex here as skills are nested.
      // This is a placeholder and needs to be implemented correctly based on your new structure.
      setSkillCategoriesData(prevCategories => {
        return prevCategories.map(cat => {
          if (cat._id === currentEditingCategoryId) {
            return {
              ...cat,
              skills: cat.skills.map(s => s._id === updatedData._id ? updatedData : s)
            };
          }
          return cat;
        });
      });
      closeEditModal();
    } catch (error) {
      console.error('Failed to update skill (via old route):', error);
      setEditError(error.message || 'Failed to update skill. Please try again.');
    }
  };

  const handleDeleteSkillFromCategory = (skill, categoryId) => {
    console.log('TODO: Delete skill:', skill, 'from category ID:', categoryId);
  };
  // --- End Admin Action Handlers ---

  return (
    <>
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-16 space-y-16 md:space-y-20">
        {/* Who I Am Section */}
        <section id="who-i-am" className="bg-white dark:bg-slate-800 p-6 sm:p-10 rounded-xl shadow-xl transition-colors duration-300">
            <h2 className="text-3xl font-semibold text-gray-800 dark:text-gray-100 mb-6">Who I Am</h2>
            <div className="space-y-4 text-gray-700 dark:text-gray-300 leading-relaxed text-base sm:text-lg">
              {aboutMeData.introduction.map((paragraph, index) => ( <p key={index}>{paragraph}</p> ))}
              {aboutMeData.philosophy && ( <p className="mt-6 italic border-l-4 border-indigo-500 dark:border-indigo-400 pl-4 py-3 bg-indigo-50 dark:bg-slate-700/60 rounded-r-md"> "{aboutMeData.philosophy}" </p> )}
            </div>
        </section>

        {/* Skills Section - Updated to use skillCategoriesData */}
        <section id="skills">
          <div className="flex flex-col sm:flex-row justify-between items-center mb-8 sm:mb-10">
              <h2 className="text-3xl font-bold text-gray-900 dark:text-white text-center sm:text-left flex-grow mb-4 sm:mb-0">
                  Technical Skills
              </h2>
              {isAdmin && ( <button onClick={handleAddCategory} className="bg-purple-500 hover:bg-purple-600 text-white font-semibold py-2 px-4 rounded-lg shadow-md text-sm"> Add New Category </button> )}
          </div>

          {loadingSkills && ( <div className="text-center py-10 dark:text-gray-300"><p>Loading skills...</p></div> )}
          {errorSkills && ( <div className="text-center py-10 text-red-500 bg-red-100 p-4 rounded-md"><p>Error: {errorSkills}</p></div> )}

          {!loadingSkills && !errorSkills && skillCategoriesData.length === 0 && (
              <div className="text-center py-10 text-gray-500 bg-gray-50 dark:bg-slate-800 p-6 rounded-xl shadow-md">
                  <p>No skill categories have been added yet.</p>
                  {isAdmin && ( <button onClick={handleAddCategory} className="mt-4 bg-purple-500 ..."> Add First Category </button> )}
              </div>
          )}

          {!loadingSkills && !errorSkills && skillCategoriesData.length > 0 && (
            <div className="space-y-10">
              {/* Assuming skillCategoriesData is an array of: { _id: 'catId', name: 'Category Name', skills: [{_id: 'skillId', name: 'Skill Name'}, ...] } */}
              {skillCategoriesData.map((category) => (
                <div key={category._id} className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-xl">
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-xl sm:text-2xl font-semibold text-indigo-600 dark:text-indigo-400">
                      {category.name} {/* Display category name */}
                    </h3>
                    {isAdmin && (
                      <div className="flex gap-2">
                        <button onClick={() => handleAddSkillToCategory(category)} title="Add Skill to this Category" className="text-green-500 hover:text-green-700 text-xs p-1">Add Skill</button>
                        <button onClick={() => handleEditCategory(category)} title="Edit Category" className="text-blue-500 hover:text-blue-700 text-xs p-1">Edit Cat.</button>
                        <button onClick={() => handleDeleteCategory(category)} title="Delete Category" className="text-red-500 hover:text-red-700 text-xs p-1">Del. Cat.</button>
                      </div>
                    )}
                  </div>
                  {(category.skills && category.skills.length > 0) ? (
                    <div className="flex flex-wrap gap-3">
                      {category.skills.map((skill) => ( // Display skills from the category
                        <div 
                            key={skill._id} 
                            className="group bg-indigo-100 dark:bg-slate-700 text-indigo-800 dark:text-indigo-300 px-4 py-2 rounded-full text-sm font-medium shadow-sm flex items-center gap-2"
                        >
                          <span>{skill.name}</span>
                          {isAdmin && (
                            <div className="flex gap-1 items-center opacity-0 group-hover:opacity-100">
                              <button onClick={() => openEditModal(skill, category._id)} title="Edit Skill" className="text-blue-600 p-1 text-xs"> E </button>
                              <button onClick={() => handleDeleteSkillFromCategory(skill, category._id)} title="Delete Skill" className="text-red-600 p-1 text-xs"> X </button>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  ) : (
                     <p className="text-sm text-gray-500 dark:text-gray-400">No skills listed in this category.</p>
                  )}
                </div>
              ))}
            </div>
          )}
        </section>

        {/* ... Other sections ... */}
      </div>

      {/* --- Edit Skill Modal (This is still based on the old flat skill structure and needs significant rework) --- */}
      {isEditModalOpen && currentEditingSkill && (
        <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center p-4 z-50">
          <div className="bg-white dark:bg-slate-800 p-6 sm:p-8 rounded-xl shadow-2xl w-full max-w-md">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-semibold text-gray-800 dark:text-white">Edit Skill in Category</h2>
              <button onClick={closeEditModal} className="text-gray-500 hover:text-gray-700 text-2xl">&times;</button>
            </div>
            <form onSubmit={handleUpdateSkillSubmit} className="space-y-6">
              <div>
                <label htmlFor="skillName" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Skill Name</label>
                <input type="text" name="name" id="skillName" value={editFormState.name} onChange={handleEditFormChange} required className="w-full px-4 py-2 border rounded-lg dark:bg-slate-700 dark:text-white" />
              </div>
              {/* The 'category' field in this form is no longer relevant if editing a skill within an existing category */}
              {/* You might add fields like 'description' or other skill-specific properties here */}
              {editError && ( <p className="text-sm text-red-600 bg-red-100 p-3 rounded-md">{editError}</p> )}
              <div className="flex justify-end gap-4 pt-2">
                <button type="button" onClick={closeEditModal} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-lg">Cancel</button>
                <button type="submit" className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-lg">Save Changes</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default AboutPage;
