// frontend/src/components/resumes/ResumeCard.js
import React from 'react';

const ResumeCard = ({ resume, onUpdate, onDelete }) => {
  const downloadUrl = `${process.env.REACT_APP_API_URL_WITHOUT_API_PREFIX || 'http://localhost:5001'}/uploads/resumes/${resume.storedFilename}`;
  // Adding a cache-busting query string for the iframe src can sometimes help with updates
  const previewUrl = `${downloadUrl}#view=fitH&toolbar=0&navpanes=0&scrollbar=0&t=${new Date().getTime()}`;


  return (
    <div className="bg-white rounded-xl shadow-2xl overflow-hidden flex flex-col">
      <div className="p-6 bg-gray-50 border-b border-gray-200">
        <h2 className="text-2xl font-bold text-gray-800 truncate" title={resume.field}>
          {resume.field}
        </h2>
        <p className="text-xs text-gray-500 mt-1">
          Original Filename: {resume.originalFilename}
        </p>
      </div>

      {/* PDF Preview Area */}
      <div className="flex-grow h-96 md:h-[500px] border-b border-gray-200 bg-gray-100"> {/* Adjust height as needed */}
        <iframe
          src={previewUrl}
          title={`Preview of ${resume.field} resume`}
          width="100%"
          height="100%"
          style={{ border: 'none' }}
          allowFullScreen
        >
          <p className="p-4 text-center text-gray-600">
            Your browser does not support embedding PDFs. 
            Please <a href={downloadUrl} download={resume.originalFilename} className="text-indigo-600 hover:underline">download the PDF</a> to view it.
          </p>
        </iframe>
      </div>

      {/* Action Buttons */}
      <div className="p-6 bg-gray-50 flex flex-col sm:flex-row justify-center items-center gap-3 sm:gap-4">
        <a
          href={downloadUrl}
          download={resume.originalFilename}
          target="_blank" // Good to keep for direct PDF view if iframe fails or for user preference
          rel="noopener noreferrer"
          className="w-full sm:w-auto text-center bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-5 rounded-lg text-sm shadow-md hover:shadow-lg transition duration-300 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75"
        >
          Download PDF
        </a>
        <button
          onClick={() => onUpdate(resume)}
          className="w-full sm:w-auto bg-yellow-400 hover:bg-yellow-500 text-gray-800 font-semibold py-2 px-5 rounded-lg text-sm shadow-md hover:shadow-lg transition duration-300 focus:outline-none focus:ring-2 focus:ring-yellow-300 focus:ring-opacity-75"
        >
          Update
        </button>
        <button
          onClick={() => onDelete(resume._id, resume.field)}
          className="w-full sm:w-auto bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-5 rounded-lg text-sm shadow-md hover:shadow-lg transition duration-300 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-opacity-75"
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default ResumeCard;
