// frontend/src/pages/ResumePage.js
import React, { useState, useEffect } from 'react';
import api from '../services/api'; // Your configured Axios instance

const ResumePage = () => {
  const [resumes, setResumes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);

  // State for the add resume form
  const [newResumeField, setNewResumeField] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);

  // State for the edit resume modal/form
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingResume, setEditingResume] = useState(null); // Stores the resume object being edited
  const [editResumeField, setEditResumeField] = useState('');
  const [editSelectedFile, setEditSelectedFile] = useState(null);

  const fetchResumes = async () => {
    setIsLoading(true);
    setError(null); // Clear previous errors before fetching
    try {
      const response = await api.get('/resumes');
      setResumes(response.data);
    } catch (err) {
      console.error("Error fetching resumes:", err);
      setError(err.response?.data?.message || "Failed to load resumes. Is the backend running?");
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchResumes();
  }, []);

  const handleDelete = async (resumeId, resumeField) => {
    if (window.confirm(`Are you sure you want to delete the ${resumeField} resume? This is permanent.`)) {
      setIsLoading(true);
      try {
        await api.delete(`/resumes/${resumeId}`);
        fetchResumes();
      } catch (err) {
        console.error("Error deleting resume:", err);
        alert(err.response?.data?.message || 'Failed to delete resume.');
        setIsLoading(false);
      }
    }
  };

  // --- EDIT FUNCTIONALITY ---
  const openEditModal = (resume) => {
    setEditingResume(resume); // Store the whole resume object
    setEditResumeField(resume.field); // Pre-fill form field
    setEditSelectedFile(null); // Clear any previously selected file for edit
    setShowEditModal(true);
    setShowAddForm(false); // Close add form if open
  };

  const closeEditModal = () => {
    setShowEditModal(false);
    setEditingResume(null);
    setEditResumeField('');
    setEditSelectedFile(null);
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    if (!editingResume || !editResumeField.trim()) {
      alert("Field/category cannot be empty.");
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('field', editResumeField);
    if (editSelectedFile) { // Only append file if a new one is selected
      formData.append('resumeFile', editSelectedFile);
    }
    // If you have customFieldName logic, add it here too if 'field' is 'Other'

    try {
      await api.put(`/resumes/${editingResume._id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      closeEditModal();
      fetchResumes(); // Refresh the list
    } catch (err) {
      console.error("Error updating resume:", err.response || err);
      alert(err.response?.data?.message || "Failed to update resume. Check console.");
    } finally {
      setIsLoading(false);
    }
  };
  // --- END EDIT FUNCTIONALITY ---


  // --- ADD FUNCTIONALITY ---
  const handleAddNewResume = async (e) => {
    e.preventDefault();
    if (!newResumeField.trim()) {
      alert("Please provide a field/category for the new resume.");
      return;
    }
    if (!selectedFile) {
      alert("Please select a PDF file to upload.");
      return;
    }
    setIsLoading(true);
    const formData = new FormData();
    formData.append('field', newResumeField);
    formData.append('resumeFile', selectedFile);

    try {
      await api.post('/resumes', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setNewResumeField('');
      setSelectedFile(null);
      if (document.getElementById('addResumeFile')) document.getElementById('addResumeFile').value = null;
      setShowAddForm(false);
      fetchResumes();
    } catch (err) {
      console.error("Error adding resume:", err.response || err);
      alert(err.response?.data?.message || "Failed to add resume. Check console.");
    } finally {
      setIsLoading(false);
    }
  };
  // --- END ADD FUNCTIONALITY ---


  if (isLoading && resumes.length === 0 && !error && !showAddForm && !showEditModal) {
    return <div className="text-center py-10">Loading resumes...</div>;
  }

  if (error && resumes.length === 0 && !showAddForm && !showEditModal) {
    return <div className="text-center py-10 text-red-500 bg-red-100 p-3 rounded">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8 relative"> {/* Added relative for modal positioning */}
      {/* ADD RESUME SECTION */}
      <div className="flex flex-col sm:flex-row justify-between items-center mb-8 gap-4">
        <h1 className="text-3xl md:text-4xl font-bold text-gray-800 text-center sm:text-left">Manage Resumes</h1>
        <button
          onClick={() => { setShowAddForm(!showAddForm); setShowEditModal(false); }}
          className="w-full sm:w-auto bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-6 rounded-lg shadow-md hover:shadow-lg transition duration-300"
        >
          {showAddForm ? 'Cancel Add' : 'Add New Resume'}
        </button>
      </div>

      {error && (resumes.length > 0 || showAddForm || showEditModal) && (
         <div className="mb-4 text-center text-red-500 bg-red-100 p-3 rounded">{error}</div>
      )}

      {showAddForm && (
        <div className="bg-white shadow-xl rounded-lg p-6 md:p-8 mb-10 border border-gray-200">
          <h2 className="text-2xl font-semibold text-gray-700 mb-6">Add New Resume</h2>
          <form onSubmit={handleAddNewResume} className="space-y-6">
            <div>
              <label htmlFor="newResumeField" className="block text-sm font-medium text-gray-700 mb-1">
                Field / Category Name <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                id="newResumeField"
                value={newResumeField}
                onChange={(e) => setNewResumeField(e.target.value)}
                required
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                placeholder="e.g., Data Scientist"
              />
            </div>
            <div>
              <label htmlFor="addResumeFile" className="block text-sm font-medium text-gray-700 mb-1">
                Upload PDF File <span className="text-red-500">*</span>
              </label>
              <input
                type="file"
                id="addResumeFile"
                accept=".pdf"
                onChange={(e) => setSelectedFile(e.target.files[0])}
                required
                className="mt-1 block w-full text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-indigo-50 file:text-indigo-700 hover:file:bg-indigo-100 cursor-pointer"
              />
            </div>
            <button
              type="submit"
              disabled={isLoading}
              className="w-full sm:w-auto bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-6 rounded-lg shadow-md hover:shadow-lg transition duration-300 disabled:opacity-70"
            >
              {isLoading ? 'Uploading...' : 'Upload Resume'}
            </button>
          </form>
        </div>
      )}

      {/* EDIT RESUME MODAL */}
      {showEditModal && editingResume && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50 flex justify-center items-center p-4">
          <div className="bg-white p-6 sm:p-8 rounded-lg shadow-xl w-full max-w-lg">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-semibold text-gray-700">Update Resume: {editingResume.field}</h2>
              <button onClick={closeEditModal} className="text-gray-400 hover:text-gray-600">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path></svg>
              </button>
            </div>
            <form onSubmit={handleEditSubmit} className="space-y-6">
              <div>
                <label htmlFor="editResumeField" className="block text-sm font-medium text-gray-700 mb-1">
                  Field / Category Name <span className="text-red-500">*</span>
                </label>
                <input
                  type="text"
                  id="editResumeField"
                  value={editResumeField}
                  onChange={(e) => setEditResumeField(e.target.value)}
                  required
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                />
              </div>
              <div>
                <p className="text-sm text-gray-600 mb-1">Current File: {editingResume.originalFilename}</p>
                <label htmlFor="editResumeFile" className="block text-sm font-medium text-gray-700 mb-1">
                  Upload New PDF (Optional - if replacing):
                </label>
                <input
                  type="file"
                  id="editResumeFile"
                  accept=".pdf"
                  onChange={(e) => setEditSelectedFile(e.target.files[0])}
                  className="mt-1 block w-full text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-indigo-50 file:text-indigo-700 hover:file:bg-indigo-100 cursor-pointer"
                />
                {editSelectedFile && <p className="text-xs text-gray-500 mt-1">New file selected: {editSelectedFile.name}</p>}
              </div>
              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={closeEditModal}
                  className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-2 px-4 rounded-lg transition duration-300"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={isLoading}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-lg shadow-md hover:shadow-lg transition duration-300 disabled:opacity-70"
                >
                  {isLoading ? 'Saving...' : 'Save Changes'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}


      {/* RESUME LIST */}
      {resumes.length === 0 && !isLoading && !showAddForm && !showEditModal && (
        <p className="text-center text-gray-500 py-10">No resumes found. Click "Add New Resume" to upload your first one!</p>
      )}
      {isLoading && resumes.length > 0 && <div className="text-center py-5 text-sm text-gray-500">Processing...</div>}
      <div className="space-y-6">
        {resumes.map((resume) => (
          <div key={resume._id} className="bg-white shadow-lg rounded-lg p-6 flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 hover:shadow-xl transition-shadow duration-300">
            <div className="flex-grow">
              <h2 className="text-xl font-semibold text-gray-700">{resume.field}</h2>
              <p className="text-sm text-gray-500">Filename: {resume.originalFilename}</p>
              <p className="text-sm text-gray-500">Uploaded: {new Date(resume.createdAt || resume.uploadDate).toLocaleDateString()}</p>
            </div>
            <div className="mt-4 sm:mt-0 flex flex-wrap flex-col sm:flex-row gap-3 items-stretch sm:items-center w-full sm:w-auto">
              <a
                href={`${process.env.REACT_APP_API_URL_WITHOUT_API_PREFIX || 'http://localhost:5001'}/uploads/resumes/${resume.storedFilename}`}
                target="_blank"
                rel="noopener noreferrer"
                className="w-full sm:w-auto text-center bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-md text-sm shadow transition duration-300"
              >
                Download
              </a>
              <button
                onClick={() => openEditModal(resume)} // Changed from handleUpdate
                className="w-full sm:w-auto bg-yellow-400 hover:bg-yellow-500 text-gray-800 font-medium py-2 px-4 rounded-md text-sm shadow transition duration-300"
              >
                Update
              </button>
              <button
                onClick={() => handleDelete(resume._id, resume.field)}
                disabled={isLoading}
                className="w-full sm:w-auto bg-red-500 hover:bg-red-600 text-white font-medium py-2 px-4 rounded-md text-sm shadow transition duration-300 disabled:opacity-70"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ResumePage;
