// frontend/src/pages/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  // Replace with your actual details
  const yourName = "Harisharan"; // Or get from a config/context if you plan to make it dynamic later
  const yourTitle = "Full Stack Developer"; // Example
  const yourSpecialization = "crafting beautiful and functional web applications";
  const profileImageUrl = "/images/profile-photo.jpg"; // Path to your image in the public/images folder

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-20">
      <div className="flex flex-col md:flex-row items-center justify-center md:justify-between gap-8 md:gap-12">

        {/* Left Column: Text Content */}
        <div className="md:w-3/5 lg:w-1/2 text-center md:text-left">
          <h1 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold text-gray-800 leading-tight mb-4">
            Hello, I'm <span className="text-indigo-600">{yourName}!</span>
          </h1>
          <p className="text-lg sm:text-xl lg:text-2xl text-gray-600 mb-8 leading-relaxed">
            A passionate {yourTitle} specializing in {yourSpecialization}. I transform ideas into reality through code and design.
          </p>
          <div className="flex flex-col sm:flex-row justify-center md:justify-start gap-4">
            <Link
              to="/projects"
              className="inline-block bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-8 rounded-lg text-md sm:text-lg shadow-lg hover:shadow-xl transition-all duration-300 ease-in-out transform hover:-translate-y-0.5"
            >
              View My Work
            </Link>
            <Link
              to="/contact"
              className="inline-block bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-3 px-8 rounded-lg text-md sm:text-lg shadow-lg hover:shadow-xl transition-all duration-300 ease-in-out transform hover:-translate-y-0.5"
            >
              Get In Touch
            </Link>
          </div>
        </div>

        {/* Right Column: Image */}
        <div className="md:w-2/5 lg:w-1/2 flex justify-center md:justify-end mt-8 md:mt-0">
          <div className="w-64 h-64 sm:w-80 sm:h-80 lg:w-96 lg:h-96 rounded-full overflow-hidden shadow-2xl border-4 border-white transform transition-all duration-500 hover:scale-105">
            <img 
              src={profileImageUrl} 
              alt={yourName} 
              className="w-full h-full object-cover" 
            />
          </div>
        </div>

      </div>

      {/* Optional: You can add further sections below this hero section */}
      {/* <section className="mt-20 md:mt-32 text-center">
        <h2 className="text-3xl font-bold text-gray-700 mb-10">What I Offer</h2>
        <div className="grid md:grid-cols-3 gap-8">
          <div className="bg-white p-6 rounded-lg shadow-lg">Service 1</div>
          <div className="bg-white p-6 rounded-lg shadow-lg">Service 2</div>
          <div className="bg-white p-6 rounded-lg shadow-lg">Service 3</div>
        </div>
      </section> 
      */}
    </div>
  );
};

export default HomePage;
