// frontend/src/App.js
import React from 'react';
import { Route, Routes } from 'react-router-dom';

// Common Components
import Navbar from './components/common/Navbar';
import Footer from './components/common/Footer';

// Public Page Components
import HomePage from './pages/HomePage'; // Assuming HomePage is in src/pages/
import AboutPage from './pages/AboutPage';
import ProjectsPage from './pages/ProjectsPage';
import ContactPage from './pages/ContactPage';
import ResumePage from './pages/ResumePage';
import LoginPage from './pages/LoginPage'; // For the /login route
import NotFoundPage from './pages/NotFoundPage'; // For catch-all
// import ProjectDetail from './components/projects/ProjectDetail'; // Or from './pages/ProjectDetailPage';

// Admin Components & Pages
import AdminLayout from './components/admin/AdminLayout';
import AdminDashboardPage from './pages/admin/AdminDashboardPage';
import ProtectedRoute from './components/routing/ProtectedRoute';

// Placeholder Admin Management Pages (you'll create these components later)
// const AdminResumesPage = () => <div className="p-4">Admin Resumes Management Page - Coming Soon!</div>;
// const AdminProjectsPage = () => <div className="p-4">Admin Projects Management Page - Coming Soon!</div>;

function App() {
  return (
    <Router> {/* Router is here, ensure AuthProvider in index.js wraps this if it uses useNavigate */}
      <div className="flex flex-col min-h-screen bg-gray-100 text-gray-800">
        {/* Navbar and Footer will be part of the public layout. 
            AdminLayout will provide its own full-screen layout.
            We can refine this later if Navbar/Footer should be part of AdminLayout too.
            For now, let's assume AdminLayout takes over the whole viewport when active.
        */}

        <Routes>
          {/* Admin Routes - These should come first if they have a more specific path like /admin */}
          <Route element={<ProtectedRoute isAdminRoute={true} />}> {/* Protects all nested admin routes */}
            <Route path="/admin" element={<AdminLayout />}> {/* Parent route for admin section, uses AdminLayout */}
              <Route index element={<AdminDashboardPage />} /> {/* Default for /admin */}
              <Route path="dashboard" element={<AdminDashboardPage />} /> {/* /admin/dashboard */}
              {/* Future Admin Management Pages:
                <Route path="resumes" element={<AdminResumesPage />} />
                <Route path="projects" element={<AdminProjectsPage />} /> 
              */}
            </Route>
          </Route>

          {/* Public Routes - Wrapped in a structure that includes Navbar and Footer */}
          <Route path="/*" element={ <PublicLayout /> } /> 
          {/* Using /* to catch all non-admin routes for the public layout. 
              Alternatively, list all public routes individually if PublicLayout is not desired.
              Or, handle Navbar/Footer visibility based on route path.
              For simplicity now, let's try individual public routes with Navbar/Footer around them.
          */}
        </Routes>
      </div>
    </Router>
  );
}

// Component to wrap public pages with Navbar and Footer
const PublicLayout = () => {
  return (
    <>
      <Navbar />
      <main className="flex-grow container mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Routes> {/* Nested Routes for public pages */}
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/projects" element={<ProjectsPage />} />
          {/* <Route path="/project/:id" element={<ProjectDetail />} /> */}
          <Route path="/resume" element={<ResumePage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route path="*" element={<NotFoundPage />} /> {/* Catch-all for non-admin, non-defined public routes */}
        </Routes>
      </main>
      <Footer />
    </>
  );
};


// Simpler structure if you prefer Navbar/Footer always present and AdminLayout just replaces the main content
// This is often easier to manage initially.
function AppAlternativeStructure() {
  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-gray-100 text-gray-800">
        <Navbar /> {/* Always present */}
        <div className="flex-grow"> {/* Wrapper for main content area */}
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<HomePage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/projects" element={<ProjectsPage />} />
            {/* <Route path="/project/:id" element={<ProjectDetail />} /> */}
            <Route path="/resume" element={<ResumePage />} />
            <Route path="/contact" element={<ContactPage />} />

            {/* Admin Routes - Protected and within AdminLayout */}
            {/* The AdminLayout itself will be the content for these routes, replacing the public page content */}
            <Route element={<ProtectedRoute isAdminRoute={true} />}>
              {/* The AdminLayout will take up the full space typically occupied by the <main> tag of public pages */}
              <Route path="/admin/*" element={<AdminLayoutWithNestedRoutes />} />
            </Route>

            <Route path="*" element={<NotFoundPage />} />
          </Routes>
        </div>
        <Footer /> {/* Always present */}
      </div>
    </Router>
  );
}

// Helper for the alternative structure to nest admin routes within AdminLayout
const AdminLayoutWithNestedRoutes = () => {
  return (
    <AdminLayout>
      <Routes>
        <Route index element={<AdminDashboardPage />} /> {/* Default for /admin */}
        <Route path="dashboard" element={<AdminDashboardPage />} /> {/* /admin/dashboard */}
        {/* Future Admin Management Pages:
          <Route path="resumes" element={<AdminResumesPage />} />
          <Route path="projects" element={<AdminProjectsPage />} /> 
        */}
         {/* Add a catch-all for /admin/* if no sub-route matches */}
        <Route path="*" element={<div className="p-4">Admin section not found.</div>} />
      </Routes>
    </AdminLayout>
  );
};


// CHOOSE ONE STRUCTURE: App or AppAlternativeStructure
// For now, let's go with the AppAlternativeStructure as it's often simpler to manage Navbar/Footer visibility.
// You can rename AppAlternativeStructure to App if you prefer it.
export default AppAlternativeStructure; // Or export default App;
