// portfolio_py/frontend/src/pages/ContactPage.js
import React, { useState, useEffect } from 'react';
// ... other imports
import ConfirmationModal from '../components/common/ConfirmationModal'; // <-- Import new modal
import * as socialLinkService from '../services/socialLinkService';

const SocialLinkFormModal = ({ show, onClose, onSave, currentLink, isLoading }) => {
    // ... existing modal logic ...
    const [modalError, setModalError] = useState(''); // <-- Add error state

    const handleSubmit = (e) => {
        e.preventDefault();
        setModalError(''); // Clear previous error
        if (!formData.name.trim() || !formData.url.trim()) { 
          setModalError('Platform Name and URL are required.'); // <-- Set error state instead of alert
          return; 
        }
        onSave(formData, currentLink?._id);
    };

    // In the JSX for this modal:
    return (
        // ...
        <form onSubmit={handleSubmit} className="space-y-4">
            {modalError && <p className="text-sm text-red-500">{modalError}</p>}
            {/* ... rest of the form fields */}
        </form>
        // ...
    )
};


const ContactPage = () => {
  // ... other states
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [linkToDelete, setLinkToDelete] = useState(null);

  // ... other functions

  const handleDelete = (link) => {
    setLinkToDelete(link);
    setShowConfirmModal(true);
  };

  const executeDelete = async () => {
      if (!linkToDelete) return;
      try {
        await socialLinkService.deleteSocialLink(linkToDelete._id);
        fetchLinks();
      } catch (err) {
        alert(`Error: ${err.response?.data?.message || err.message}`);
      } finally {
        setShowConfirmModal(false);
        setLinkToDelete(null);
      }
  };

  return (
    <>
      <ConfirmationModal
        isOpen={showConfirmModal}
        onClose={() => setShowConfirmModal(false)}
        onConfirm={executeDelete}
        title="Delete Social Link"
        message={`Are you sure you want to delete the link for "${linkToDelete?.name}"?`}
      />
      {/* ... The rest of your page JSX ... */}
      {/* In the social links map: */}
      <button onClick={() => handleDelete(link)} className="...">
        <Trash2 size={12} />
      </button>
    </>
  );
};

export default ContactPage;
