// frontend/src/App.js
import React from 'react';
// BrowserRouter as Router is NO LONGER imported here, as it's handled in index.js
import { Route, Routes } from 'react-router-dom';

// Common Components
import Navbar from './components/common/Navbar';
import Footer from './components/common/Footer';

// Public Page Components
import HomePage from './components/common/HomePage';
import AboutPage from './pages/AboutPage';
import ProjectsPage from './pages/ProjectsPage';
import ContactPage from './pages/ContactPage';
import ResumePage from './pages/ResumePage';
import LoginPage from './pages/LoginPage';
import NotFoundPage from './pages/NotFoundPage';
// import ProjectDetail from './components/projects/ProjectDetail';

// Admin Components & Pages
import AdminLayout from './components/admin/AdminLayout';
import AdminDashboardPage from './pages/admin/AdminDashboardPage';
import ProtectedRoute from './components/routing/ProtectedRoute';

// Placeholder Admin Management Pages (you'll create these components later)
// const AdminResumesPage = () => <div className="p-4">Admin Resumes Management Page - Coming Soon!</div>;
// const AdminProjectsPage = () => <div className="p-4">Admin Projects Management Page - Coming Soon!</div>;


// This structure assumes Navbar and Footer are always present.
// AdminLayout will render within the main content area defined here.
function App() { // Renamed from AppAlternativeStructure for simplicity as it's the default export
  return (
    // NO <Router> here, as it's provided by frontend/src/index.js
    <div className="flex flex-col min-h-screen bg-gray-100 text-gray-800">
      <Navbar /> {/* Always present */}
      <div className="flex-grow"> {/* Wrapper for main content area */}
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/projects" element={<ProjectsPage />} />
          {/* <Route path="/project/:id" element={<ProjectDetail />} /> */}
          <Route path="/resume" element={<ResumePage />} />
          <Route path="/contact" element={<ContactPage />} />

          {/* Admin Routes - Protected and using AdminLayoutWithNestedRoutes helper */}
          <Route element={<ProtectedRoute isAdminRoute={true} />}>
            <Route path="/admin/*" element={<AdminLayoutWithNestedRoutes />} />
          </Route>

          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </div>
      <Footer /> {/* Always present */}
    </div>
    // NO </Router> here
  );
}

// Helper component to ensure AdminLayout wraps all admin sub-routes
const AdminLayoutWithNestedRoutes = () => {
  return (
    <AdminLayout> {/* AdminLayout provides the sidebar and its own <Outlet /> for content */}
      <Routes>
        <Route index element={<AdminDashboardPage />} /> {/* Default for /admin */}
        <Route path="dashboard" element={<AdminDashboardPage />} /> {/* /admin/dashboard */}
        {/* Future Admin Management Pages will be routed here:
          e.g., <Route path="resumes" element={<AdminResumesPage />} />
          e.g., <Route path="projects" element={<AdminProjectsPage />} /> 
        */}
        {/* Catch-all for any /admin/* sub-routes not explicitly defined */}
        <Route path="*" element={
          <div className="p-6 text-center">
            <h2 className="text-xl font-semibold text-red-500">Admin Section Not Found</h2>
            <p className="text-gray-600">The specific admin page you're looking for doesn't exist.</p>
          </div>
        } />
      </Routes>
    </AdminLayout>
  );
};

export default App;
